import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Grid,
  Paper,
  Typography,
  Box,
  Card,
  CardContent,
  Button,
  Tab,
  Tabs,
  CircularProgress,
  Fab
} from '@mui/material';
import {
  Assignment as TicketIcon,
  Add as AddIcon,
  CheckCircle as ResolvedIcon,
  Pending as PendingIcon
} from '@mui/icons-material';
import axios from 'axios';
import { useAuth } from '../../contexts/AuthContext';
import TicketList from '../../components/tickets/TicketList';
import { API_URL } from '../../utils/apiConfig';

const StatCard = ({ title, value, icon, color }) => (
  <Card>
    <CardContent>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
        {React.cloneElement(icon, { sx: { color, mr: 1 } })}
        <Typography color="textSecondary" variant="h6">
          {title}
        </Typography>
      </Box>
      <Typography variant="h4">{value}</Typography>
    </CardContent>
  </Card>
);

const ClientDashboard = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    totalTickets: 0,
    activeTickets: 0,
    resolvedTickets: 0
  });
  const [tickets, setTickets] = useState([]);
  const [selectedTab, setSelectedTab] = useState(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  // Function to add a new ticket to the state without refetching
  const addNewTicket = useCallback((newTicket) => {
    console.log('Adding new ticket to dashboard:', newTicket);

    // Update the tickets array
    setTickets(prevTickets => [newTicket, ...prevTickets]);

    // Update the stats
    setStats(prevStats => ({
      totalTickets: prevStats.totalTickets + 1,
      activeTickets: prevStats.activeTickets + 1,
      resolvedTickets: prevStats.resolvedTickets
    }));

    console.log('Ticket added to local state successfully');
  }, []);

  const fetchDashboardData = async () => {
    try {
      console.log('Fetching dashboard data using API URL:', API_URL);
      console.log('User token:', localStorage.getItem('token'));
      console.log('User data:', localStorage.getItem('user'));

      // Use the correct endpoints that match the backend routes
      const [statsRes, ticketsRes] = await Promise.all([
        axios.get(`${API_URL}/tickets/stats`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        }),
        axios.get(`${API_URL}/tickets`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        })
      ]);

      console.log('Stats response:', statsRes.data);
      console.log('Tickets response:', ticketsRes);
      console.log('Tickets data type:', typeof ticketsRes.data);
      console.log('Is tickets array:', Array.isArray(ticketsRes.data));
      console.log('Number of tickets received:', ticketsRes.data ? ticketsRes.data.length : 0);

      // Debug each ticket to check structure
      if (Array.isArray(ticketsRes.data) && ticketsRes.data.length > 0) {
        console.log('First ticket sample:', ticketsRes.data[0]);
      }

      // Check if tickets is an array
      if (Array.isArray(ticketsRes.data)) {
        setTickets(ticketsRes.data);
        console.log('Tickets state set with data from API');
      } else {
        console.error('Expected tickets data to be an array but got:', typeof ticketsRes.data);
        setTickets([]);
      }

      setStats(statsRes.data);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      console.error('Error details:', error.response?.data || error.message);
      setTickets([]);
    } finally {
      setLoading(false);
    }
  };

  const filterTickets = () => {
    switch (selectedTab) {
      case 0: // All
        return tickets;
      case 1: // Active
        return tickets.filter(ticket => 
          ['new', 'in_progress', 'on_hold'].includes(ticket.status)
        );
      case 2: // Resolved
        return tickets.filter(ticket => ticket.status === 'resolved');
      default:
        return tickets;
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 4 }}>
        <Typography variant="h4">
          Welcome, {user.name}
        </Typography>
        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={() => navigate('/client/tickets/create')}
        >
          Create New Ticket
        </Button>
      </Box>

      <Grid container spacing={3}>
        {/* Stats Cards */}
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Total Tickets"
            value={stats.totalTickets}
            icon={<TicketIcon />}
            color="primary.main"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Active Tickets"
            value={stats.activeTickets}
            icon={<PendingIcon />}
            color="warning.main"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Resolved Tickets"
            value={stats.resolvedTickets}
            icon={<ResolvedIcon />}
            color="success.main"
          />
        </Grid>

        {/* Company Information */}
        <Grid item xs={12}>
          <Paper sx={{ p: 2, mb: 3 }}>
            <Typography variant="h6" sx={{ mb: 2 }}>Company Information</Typography>
            <Grid container spacing={2}>
              <Grid item xs={12} md={4}>
                <Typography variant="subtitle2" color="textSecondary">Company Name</Typography>
                <Typography variant="body1">{user.company}</Typography>
              </Grid>
              <Grid item xs={12} md={4}>
                <Typography variant="subtitle2" color="textSecondary">Department</Typography>
                <Typography variant="body1">{user.department}</Typography>
              </Grid>
              <Grid item xs={12} md={4}>
                <Typography variant="subtitle2" color="textSecondary">Contact</Typography>
                <Typography variant="body1">{user.phone}</Typography>
              </Grid>
            </Grid>
          </Paper>
        </Grid>

        {/* Tickets Section */}
        <Grid item xs={12}>
          <Paper sx={{ p: 2 }}>
            <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 2 }}>
              <Tabs value={selectedTab} onChange={(e, newValue) => setSelectedTab(newValue)}>
                <Tab label="All Tickets" />
                <Tab label="Active" />
                <Tab label="Resolved" />
              </Tabs>
            </Box>
            <TicketList tickets={filterTickets()} />
          </Paper>
        </Grid>
      </Grid>

      {/* Floating Action Button for mobile */}
      <Box sx={{ display: { sm: 'none' } }}>
        <Fab
          color="primary"
          sx={{ position: 'fixed', bottom: 16, right: 16 }}
          onClick={() => navigate('/client/tickets/create')}
        >
          <AddIcon />
        </Fab>
      </Box>
    </Box>
  );
};

export default ClientDashboard;
