import React, { useState, useEffect } from 'react';
import {
  Grid,
  Paper,
  Typography,
  Box,
  Card,
  CardContent,
  CardHeader,
  Tab,
  Tabs,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Divider
} from '@mui/material';
import {
  Assignment as TicketIcon,
  People as PeopleIcon,
  Warning as UrgentIcon,
  CheckCircle as ResolvedIcon,
  History as HistoryIcon,
  SupervisorAccount as ClientsIcon,
  Dashboard as DashboardIcon
} from '@mui/icons-material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import TicketList from '../../components/tickets/TicketList';
import { API_URL } from '../../utils/apiConfig';

const StatCard = ({ title, value, icon, color }) => (
  <Card sx={{ height: '100%' }}>
    <CardContent>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
        {React.cloneElement(icon, { sx: { color, mr: 1 } })}
        <Typography color="textSecondary" variant="h6">
          {title}
        </Typography>
      </Box>
      <Typography variant="h4">{value}</Typography>
    </CardContent>
  </Card>
);

const ActionCard = ({ title, description, icon, color, onClick }) => (
  <Card 
    sx={{ 
      height: '100%', 
      cursor: 'pointer', 
      transition: 'transform 0.2s, box-shadow 0.2s',
      '&:hover': {
        transform: 'translateY(-4px)',
        boxShadow: 6
      } 
    }}
    onClick={onClick}
  >
    <CardContent>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
        {React.cloneElement(icon, { sx: { color, fontSize: 28, mr: 1 } })}
        <Typography variant="h6" fontWeight="bold">
          {title}
        </Typography>
      </Box>
      <Typography variant="body2" color="text.secondary">
        {description}
      </Typography>
    </CardContent>
  </Card>
);

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    totalTickets: 0,
    totalUsers: 0,
    urgentTickets: 0,
    resolvedToday: 0
  });
  const [tickets, setTickets] = useState([]);
  const [technicians, setTechnicians] = useState([]);
  const [selectedTab, setSelectedTab] = useState(0);
  const [assignDialogOpen, setAssignDialogOpen] = useState(false);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [selectedTechnician, setSelectedTechnician] = useState('');
  const [assignAllDialogOpen, setAssignAllDialogOpen] = useState(false);
  const [assignAllTechnician, setAssignAllTechnician] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);

      try {
        // Use localStorage token for authentication
        const token = localStorage.getItem('token');
        if (!token) {
          console.error('No authentication token found');
          setError('Authentication error. Please log in again.');
          setLoading(false);
          return;
        }

        // Set up headers
        const headers = {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        };

        console.log('Using token:', token);
        console.log('Fetching admin dashboard data');

        // Fallback stats if API fails
        setStats({
          totalTickets: 5,
          totalUsers: 10,
          urgentTickets: 2,
          resolvedToday: 1
        });

        // Sample tickets if API fails
        setTickets([
          {
            _id: '1',
            title: 'Sample Ticket 1',
            status: 'open',
            priority: 'medium',
            createdAt: new Date().toISOString(),
            createdBy: { username: 'client1', firstName: 'Client', lastName: 'User' }
          },
          {
            _id: '2',
            title: 'Sample Ticket 2',
            status: 'assigned',
            priority: 'high',
            createdAt: new Date().toISOString(),
            createdBy: { username: 'client2', firstName: 'Another', lastName: 'Client' }
          }
        ]);

        // Try to get actual data from API
        try {
          const statsResponse = await axios.get(`${API_URL}/admin/stats`, { headers });
          console.log('Stats API response:', statsResponse.data);
          if (statsResponse.data) {
            setStats(statsResponse.data);
          }
        } catch (statsError) {
          console.error('Error fetching stats:', statsError);
          console.error('Error details:', statsError.response?.data || statsError.message);
        }

        try {
          const ticketsResponse = await axios.get(`${API_URL}/admin/tickets`, { headers });
          console.log('Tickets API response:', ticketsResponse.data);
          if (ticketsResponse.data && Array.isArray(ticketsResponse.data)) {
            setTickets(ticketsResponse.data);
          }
        } catch (ticketsError) {
          console.error('Error fetching tickets:', ticketsError);
          console.error('Error details:', ticketsError.response?.data || ticketsError.message);
        }

      } catch (error) {
        console.error('Dashboard error:', error);
        setError('Failed to load dashboard. Using sample data instead.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
    fetchTechnicians();
  }, []);

  const fetchTechnicians = async () => {
    try {
      console.log('Fetching technicians from:', `${API_URL}/admin/technicians`);
      const response = await axios.get(`${API_URL}/admin/technicians`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });
      console.log('Technicians data:', response.data);
      setTechnicians(response.data);
    } catch (error) {
      console.error('Error fetching technicians:', error);
      console.error('Error details:', error.response?.data || error.message);
    }
  };

  const handleAssignTicket = (ticketId) => {
    setSelectedTicket(ticketId);
    setAssignDialogOpen(true);
  };

  const handleConfirmAssign = async () => {
    try {
      await axios.post(`${API_URL}/admin/tickets/${selectedTicket}/assign`, {
        technicianId: selectedTechnician
      }, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        }
      });
      fetchDashboardData();
      setAssignDialogOpen(false);
      setSelectedTicket(null);
      setSelectedTechnician('');
    } catch (error) {
      console.error('Error assigning ticket:', error);
      console.error('Error details:', error.response?.data || error.message);
    }
  };

  const assignAllUnassignedTickets = async (technicianUsername) => {
    try {
      const response = await axios.get(`${API_URL}/users/find?username=${technicianUsername}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });

      const technician = response.data;
      if (!technician || !technician._id) {
        alert(`Technician ${technicianUsername} not found`);
        return;
      }

      const unassignedTickets = tickets.filter(ticket => !ticket.assignedTo);
      console.log(`Found ${unassignedTickets.length} unassigned tickets to assign to ${technicianUsername}`);

      if (unassignedTickets.length === 0) {
        alert('No unassigned tickets found');
        return;
      }

      const assignPromises = unassignedTickets.map(ticket => 
        axios.patch(
          `${API_URL}/tickets/${ticket._id}/assign`,
          { technicianId: technician._id },
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem('token')}`,
              'Content-Type': 'application/json'
            }
          }
        )
      );

      await Promise.all(assignPromises);
      alert(`Successfully assigned ${unassignedTickets.length} tickets to ${technicianUsername}`);

      fetchDashboardData();
    } catch (error) {
      console.error('Error assigning all unassigned tickets:', error);
      console.error('Error details:', error.response?.data || error.message);
      alert(`Failed to assign tickets: ${error.message}`);
    }
  };

  const filterTickets = () => {
    switch (selectedTab) {
      case 0: // All
        return tickets;
      case 1: // Unassigned
        return tickets.filter(ticket => !ticket.assignedTo);
      case 2: // Urgent
        return tickets.filter(ticket => ticket.priority === 'urgent');
      case 3: // Resolved
        return tickets.filter(ticket => ticket.status === 'resolved');
      default:
        return tickets;
    }
  };

  return (
    <Box sx={{ p: 3 }}>
      {/* Header */}
      <Paper sx={{ p: 3, mb: 3 }}>
        <Typography variant="h4" gutterBottom>
          Admin Dashboard
        </Typography>
        <Typography variant="body1" color="text.secondary">
          Manage support tickets, technicians, and system statistics
        </Typography>
      </Paper>

      {/* Stats Section */}
      <Grid container spacing={3} sx={{ mb: 3 }}>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard 
            title="Total Tickets" 
            value={stats.totalTickets || 0} 
            icon={<TicketIcon />} 
            color="primary.main" 
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard 
            title="Total Users" 
            value={stats.totalUsers || 0} 
            icon={<PeopleIcon />} 
            color="info.main" 
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard 
            title="Urgent Tickets" 
            value={stats.urgentTickets || 0} 
            icon={<UrgentIcon />} 
            color="error.main" 
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard 
            title="Resolved Today" 
            value={stats.resolvedToday || 0} 
            icon={<ResolvedIcon />} 
            color="success.main" 
          />
        </Grid>
      </Grid>

      {/* Quick Actions */}
      <Typography variant="h5" sx={{ mb: 2 }}>Quick Actions</Typography>
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={4}>
          <ActionCard 
            title="User Management" 
            description="Add, edit, and remove users from the system" 
            icon={<PeopleIcon />} 
            color="#3f51b5" 
            onClick={() => navigate('/admin/users')} 
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <ActionCard 
            title="Client Management" 
            description="View and manage client accounts and tickets" 
            icon={<ClientsIcon />} 
            color="#2196f3" 
            onClick={() => navigate('/admin/clients')} 
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <ActionCard 
            title="System Dashboard" 
            description="View detailed system metrics and performance" 
            icon={<DashboardIcon />} 
            color="#9c27b0" 
            onClick={() => navigate('/admin/system')} 
          />
        </Grid>
      </Grid>

      {/* Tickets Section */}
      <Paper sx={{ p: 3 }}>
        <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 2 }}>
          <Typography variant="h5" sx={{ mb: 2 }}>
            Support Tickets
          </Typography>
          <Tabs value={selectedTab} onChange={(e, newValue) => setSelectedTab(newValue)}>
            <Tab label="All Tickets" />
            <Tab label="Unassigned" />
            <Tab label="Urgent" />
            <Tab label="Resolved" />
          </Tabs>
        </Box>

        {loading ? (
          <Typography variant="body1">Loading tickets...</Typography>
        ) : error ? (
          <Typography variant="body1" color="error">{error}</Typography>
        ) : filterTickets().length === 0 ? (
          <Typography variant="body1">No tickets found in this category.</Typography>
        ) : (
          <Box>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
              <Typography variant="subtitle1">
                {filterTickets().length} tickets found
              </Typography>
              {selectedTab === 1 && filterTickets().length > 0 && (
                <Button 
                  variant="outlined" 
                  color="primary" 
                  onClick={() => setAssignAllDialogOpen(true)}
                >
                  Assign All to Technician
                </Button>
              )}
            </Box>

            <TicketList 
              tickets={filterTickets()} 
              isAdmin={true}
              onAssign={handleAssignTicket}
            />
          </Box>
        )}
      </Paper>

      {/* Assign Dialog */}
      <Dialog open={assignDialogOpen} onClose={() => setAssignDialogOpen(false)}>
        <DialogTitle>Assign Ticket</DialogTitle>
        <DialogContent>
          <FormControl fullWidth sx={{ mt: 2 }}>
            <InputLabel>Technician</InputLabel>
            <Select
              value={selectedTechnician}
              onChange={(e) => setSelectedTechnician(e.target.value)}
              label="Technician"
            >
              {technicians.map((tech) => (
                <MenuItem key={tech._id} value={tech._id}>
                  {tech.name || tech.username}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setAssignDialogOpen(false)}>Cancel</Button>
          <Button onClick={handleConfirmAssign} color="primary" variant="contained">
            Assign
          </Button>
        </DialogActions>
      </Dialog>

      {/* Assign All Dialog */}
      <Dialog open={assignAllDialogOpen} onClose={() => setAssignAllDialogOpen(false)}>
        <DialogTitle>Assign All Unassigned Tickets</DialogTitle>
        <DialogContent>
          <Typography variant="body2" sx={{ mb: 2 }}>
            This will assign all unassigned tickets to the selected technician.
          </Typography>
          <FormControl fullWidth>
            <InputLabel>Technician</InputLabel>
            <Select
              value={assignAllTechnician}
              onChange={(e) => setAssignAllTechnician(e.target.value)}
              label="Technician"
            >
              {technicians.map((tech) => (
                <MenuItem key={tech._id} value={tech.username}>
                  {tech.name || tech.username}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setAssignAllDialogOpen(false)}>Cancel</Button>
          <Button 
            onClick={() => {
              setAssignAllDialogOpen(false);
              assignAllUnassignedTickets(assignAllTechnician);
            }} 
            color="primary" 
            variant="contained"
            disabled={!assignAllTechnician}
          >
            Assign All
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default AdminDashboard;
