import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Paper,
  Stepper,
  Step,
  StepLabel,
  Typography,
  Box,
  Button,
  FormControl,
  FormLabel,
  RadioGroup,
  Radio,
  FormControlLabel,
  TextField,
  MenuItem,
  FormHelperText,
  Alert
} from '@mui/material';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import axios from 'axios';

const steps = ['Problem Type', 'Product Details', 'Additional Information'];

const softwareProducts = [
  'Kaspersky',
  'Sophos',
  'Fortinet',
  'Barracuda',
  'Other'
];

const hardwareCategories = [
  'Server',
  'Network Equipment',
  'Storage Device',
  'Security Appliance',
  'Other'
];

const CreateTicket = () => {
  const navigate = useNavigate();
  const [activeStep, setActiveStep] = useState(0);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    type: '',
    productCategory: '',
    productName: '',
    serialNumber: '',
    warrantyStatus: false,
    warrantyExpiry: null,
    description: '',
    priority: 'medium'
  });

  const handleNext = () => {
    if (validateStep()) {
      setActiveStep((prevStep) => prevStep + 1);
    }
  };

  const handleBack = () => {
    setActiveStep((prevStep) => prevStep - 1);
  };

  const validateStep = () => {
    switch (activeStep) {
      case 0:
        if (!formData.type) {
          setError('Please select a problem type');
          return false;
        }
        break;
      case 1:
        if (!formData.productCategory) {
          setError('Please select a product category');
          return false;
        }
        if (!formData.productName) {
          setError('Please enter a product name');
          return false;
        }
        break;
      case 2:
        if (!formData.description) {
          setError('Please provide a description');
          return false;
        }
        break;
    }
    setError('');
    return true;
  };

  const handleSubmit = async () => {
    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/tickets`, formData);
      navigate(`/tickets/${response.data._id}`);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to create ticket');
    }
  };

  const handleChange = (field) => (event) => {
    setFormData({
      ...formData,
      [field]: event.target.value
    });
  };

  const renderStepContent = (step) => {
    switch (step) {
      case 0:
        return (
          <Box sx={{ mt: 2 }}>
            <FormControl component="fieldset" required>
              <FormLabel component="legend">What type of problem are you experiencing?</FormLabel>
              <RadioGroup
                value={formData.type}
                onChange={handleChange('type')}
              >
                <FormControlLabel value="software" control={<Radio />} label="Software Problem" />
                <FormControlLabel value="hardware" control={<Radio />} label="Hardware Problem" />
              </RadioGroup>
            </FormControl>
          </Box>
        );

      case 1:
        return (
          <Box sx={{ mt: 2 }}>
            <FormControl fullWidth sx={{ mb: 2 }}>
              <FormLabel>Product Category</FormLabel>
              {formData.type === 'software' ? (
                <TextField
                  select
                  value={formData.productCategory}
                  onChange={handleChange('productCategory')}
                  fullWidth
                  margin="normal"
                >
                  {softwareProducts.map((product) => (
                    <MenuItem key={product} value={product}>
                      {product}
                    </MenuItem>
                  ))}
                </TextField>
              ) : (
                <TextField
                  select
                  value={formData.productCategory}
                  onChange={handleChange('productCategory')}
                  fullWidth
                  margin="normal"
                >
                  {hardwareCategories.map((category) => (
                    <MenuItem key={category} value={category}>
                      {category}
                    </MenuItem>
                  ))}
                </TextField>
              )}
            </FormControl>

            {formData.productCategory === 'Other' && (
              <TextField
                fullWidth
                label="Specify Product"
                value={formData.productName}
                onChange={handleChange('productName')}
                margin="normal"
              />
            )}

            <TextField
              fullWidth
              label="Serial Number"
              value={formData.serialNumber}
              onChange={handleChange('serialNumber')}
              margin="normal"
            />

            <FormControl component="fieldset" sx={{ mt: 2 }}>
              <FormLabel component="legend">Warranty Status</FormLabel>
              <RadioGroup
                value={formData.warrantyStatus.toString()}
                onChange={(e) => setFormData({
                  ...formData,
                  warrantyStatus: e.target.value === 'true'
                })}
              >
                <FormControlLabel value="true" control={<Radio />} label="Under Warranty" />
                <FormControlLabel value="false" control={<Radio />} label="Out of Warranty" />
              </RadioGroup>
            </FormControl>

            {formData.warrantyStatus && (
              <LocalizationProvider dateAdapter={AdapterDateFns}>
                <DatePicker
                  label="Warranty Expiry Date"
                  value={formData.warrantyExpiry}
                  onChange={(date) => setFormData({
                    ...formData,
                    warrantyExpiry: date
                  })}
                  renderInput={(params) => <TextField {...params} fullWidth sx={{ mt: 2 }} />}
                />
              </LocalizationProvider>
            )}
          </Box>
        );

      case 2:
        return (
          <Box sx={{ mt: 2 }}>
            <FormControl fullWidth sx={{ mb: 2 }}>
              <FormLabel>Priority Level</FormLabel>
              <RadioGroup
                value={formData.priority}
                onChange={handleChange('priority')}
              >
                <FormControlLabel value="low" control={<Radio />} label="Low - Minor issue, no impact on operations" />
                <FormControlLabel value="medium" control={<Radio />} label="Medium - Moderate impact on operations" />
                <FormControlLabel value="high" control={<Radio />} label="High - Significant impact on operations" />
                <FormControlLabel value="urgent" control={<Radio />} label="Urgent - Critical issue, operations halted" />
              </RadioGroup>
            </FormControl>

            <TextField
              fullWidth
              multiline
              rows={4}
              label="Detailed Description"
              value={formData.description}
              onChange={handleChange('description')}
              placeholder="Please provide detailed information about the issue..."
              margin="normal"
            />
          </Box>
        );

      default:
        return null;
    }
  };

  return (
    <Container maxWidth="md" sx={{ mt: 4, mb: 4 }}>
      <Paper sx={{ p: 4 }}>
        <Typography component="h1" variant="h4" align="center" gutterBottom>
          Create New Support Ticket
        </Typography>

        <Stepper activeStep={activeStep} sx={{ mt: 3, mb: 5 }}>
          {steps.map((label) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
            </Step>
          ))}
        </Stepper>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        {renderStepContent(activeStep)}

        <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 3 }}>
          {activeStep !== 0 && (
            <Button onClick={handleBack} sx={{ mr: 1 }}>
              Back
            </Button>
          )}
          {activeStep === steps.length - 1 ? (
            <Button
              variant="contained"
              color="primary"
              onClick={handleSubmit}
            >
              Submit Ticket
            </Button>
          ) : (
            <Button
              variant="contained"
              color="primary"
              onClick={handleNext}
            >
              Next
            </Button>
          )}
        </Box>
      </Paper>
    </Container>
  );
};

export default CreateTicket;
