import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  Box,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Button,
  Stepper,
  Step,
  StepLabel,
  Grid,
  Alert,
  CircularProgress
} from '@mui/material';
import axios from 'axios';
import { useAuth } from '../../contexts/AuthContext';

const steps = ['Select Category', 'Product Details', 'Issue Details'];

const CreateTicket = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [activeStep, setActiveStep] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    category: '',
    productType: '',
    productName: '',
    serialNumber: '',
    warrantyStatus: false,
    warrantyExpiry: '',
    priority: 'medium',
    description: '',
    attachments: []
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleNext = () => {
    if (validateStep()) {
      setActiveStep((prevStep) => prevStep + 1);
    }
  };

  const handleBack = () => {
    setActiveStep((prevStep) => prevStep - 1);
  };

  const validateStep = () => {
    setError('');
    switch (activeStep) {
      case 0:
        if (!formData.category) {
          setError('Please select a category');
          return false;
        }
        break;
      case 1:
        if (formData.category === 'software' && !formData.productType) {
          setError('Please select a product type');
          return false;
        }
        if (formData.category === 'hardware' && !formData.serialNumber) {
          setError('Please enter the serial number');
          return false;
        }
        if (!formData.productName) {
          setError('Please enter the product name');
          return false;
        }
        break;
      case 2:
        if (!formData.description) {
          setError('Please provide a description of the issue');
          return false;
        }
        break;
      default:
        break;
    }
    return true;
  };

  const handleSubmit = async () => {
    if (!validateStep()) return;

    setLoading(true);
    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/tickets`, {
        ...formData,
        client: user._id
      });
      navigate(`/tickets/${response.data._id}`);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to create ticket');
      setLoading(false);
    }
  };

  const renderStepContent = (step) => {
    switch (step) {
      case 0:
        return (
          <Box sx={{ mt: 2 }}>
            <FormControl fullWidth required>
              <InputLabel>Category</InputLabel>
              <Select
                name="category"
                value={formData.category}
                onChange={handleChange}
                label="Category"
              >
                <MenuItem value="software">Software Solution</MenuItem>
                <MenuItem value="hardware">Hardware Solution</MenuItem>
              </Select>
            </FormControl>
          </Box>
        );

      case 1:
        return (
          <Box sx={{ mt: 2 }}>
            {formData.category === 'software' && (
              <FormControl fullWidth required sx={{ mb: 2 }}>
                <InputLabel>Product Type</InputLabel>
                <Select
                  name="productType"
                  value={formData.productType}
                  onChange={handleChange}
                  label="Product Type"
                >
                  <MenuItem value="kaspersky">Kaspersky</MenuItem>
                  <MenuItem value="sophos">Sophos</MenuItem>
                  <MenuItem value="forti">Forti</MenuItem>
                  <MenuItem value="other">Other</MenuItem>
                </Select>
              </FormControl>
            )}

            <TextField
              fullWidth
              required
              label="Product Name"
              name="productName"
              value={formData.productName}
              onChange={handleChange}
              sx={{ mb: 2 }}
            />

            {formData.category === 'hardware' && (
              <>
                <TextField
                  fullWidth
                  required
                  label="Serial Number"
                  name="serialNumber"
                  value={formData.serialNumber}
                  onChange={handleChange}
                  sx={{ mb: 2 }}
                />
                <Grid container spacing={2}>
                  <Grid item xs={12} sm={6}>
                    <FormControl fullWidth required>
                      <InputLabel>Warranty Status</InputLabel>
                      <Select
                        name="warrantyStatus"
                        value={formData.warrantyStatus}
                        onChange={handleChange}
                        label="Warranty Status"
                      >
                        <MenuItem value={true}>In Warranty</MenuItem>
                        <MenuItem value={false}>Out of Warranty</MenuItem>
                      </Select>
                    </FormControl>
                  </Grid>
                  {formData.warrantyStatus && (
                    <Grid item xs={12} sm={6}>
                      <TextField
                        fullWidth
                        required
                        type="date"
                        label="Warranty Expiry"
                        name="warrantyExpiry"
                        value={formData.warrantyExpiry}
                        onChange={handleChange}
                        InputLabelProps={{ shrink: true }}
                      />
                    </Grid>
                  )}
                </Grid>
              </>
            )}
          </Box>
        );

      case 2:
        return (
          <Box sx={{ mt: 2 }}>
            <FormControl fullWidth required sx={{ mb: 2 }}>
              <InputLabel>Priority</InputLabel>
              <Select
                name="priority"
                value={formData.priority}
                onChange={handleChange}
                label="Priority"
              >
                <MenuItem value="low">Low</MenuItem>
                <MenuItem value="medium">Medium</MenuItem>
                <MenuItem value="high">High</MenuItem>
                <MenuItem value="urgent">Urgent</MenuItem>
              </Select>
            </FormControl>

            <TextField
              fullWidth
              required
              multiline
              rows={4}
              label="Description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              placeholder="Please provide detailed information about the issue..."
            />
          </Box>
        );

      default:
        return null;
    }
  };

  return (
    <Container maxWidth="md">
      <Paper sx={{ p: 3, mt: 3 }}>
        <Typography variant="h5" gutterBottom>
          Create New Support Ticket
        </Typography>

        <Stepper activeStep={activeStep} sx={{ mt: 3, mb: 4 }}>
          {steps.map((label) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
            </Step>
          ))}
        </Stepper>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        {renderStepContent(activeStep)}

        <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3 }}>
          <Button
            disabled={activeStep === 0}
            onClick={handleBack}
          >
            Back
          </Button>
          <Box>
            <Button
              variant="contained"
              onClick={activeStep === steps.length - 1 ? handleSubmit : handleNext}
              disabled={loading}
            >
              {loading ? (
                <CircularProgress size={24} />
              ) : activeStep === steps.length - 1 ? (
                'Submit'
              ) : (
                'Next'
              )}
            </Button>
          </Box>
        </Box>
      </Paper>
    </Container>
  );
};

export default CreateTicket;
