import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Paper,
  Button,
  Typography,
  Box,
  Alert,
  Divider,
  CircularProgress
} from '@mui/material';
import { AdminPanelSettings, Security, Done } from '@mui/icons-material';

const LoginBypass = () => {
  const navigate = useNavigate();
  const [status, setStatus] = useState('pending');
  const [error, setError] = useState('');

  useEffect(() => {
    // Auto-execute login bypass on component mount
    bypassLogin();
  }, []);

  const bypassLogin = () => {
    try {
      // Admin token created from our fix script
      const adminToken = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY3Y2FkMWZiMzNiYWFlNjQ4ZDI2ZTUzYiIsInJvbGUiOiJhZG1pbiIsImlhdCI6MTc0MTM0ODAxMCwiZXhwIjoxNzQxOTUyODEwfQ.D5ab_dhAdpVJqN0uXKavwchxu5b8B1riMqhRWHkYe3E';

      // Admin user data
      const adminUser = {
        _id: '67cad1fb33baae648d26e53b',
        username: 'admin',
        email: 'admin@beta-tech.com',
        role: 'admin'
      };

      // Save to localStorage
      localStorage.setItem('token', adminToken);
      localStorage.setItem('user', JSON.stringify(adminUser));

      // Update status
      setStatus('success');

      // Redirect after a short delay
      setTimeout(() => {
        navigate('/admin');
      }, 2000);
    } catch (err) {
      console.error('Bypass login error:', err);
      setStatus('error');
      setError(err.message || 'Unknown error occurred');
    }
  };

  return (
    <Container component="main" maxWidth="sm" sx={{ mt: 8 }}>
      <Paper elevation={3} sx={{ p: 4, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <AdminPanelSettings color="primary" sx={{ fontSize: 64, mb: 2 }} />
        <Typography component="h1" variant="h4" gutterBottom>
          Admin Login Bypass
        </Typography>
        <Divider sx={{ width: '100%', mb: 3 }} />

        {status === 'pending' && (
          <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', my: 3 }}>
            <CircularProgress size={40} sx={{ mb: 2 }} />
            <Typography variant="body1">Setting up automatic login...</Typography>
          </Box>
        )}

        {status === 'success' && (
          <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', my: 3 }}>
            <Done color="success" sx={{ fontSize: 48, mb: 2 }} />
            <Typography variant="body1" gutterBottom>Automatic login successful!</Typography>
            <Alert severity="success" sx={{ width: '100%', mt: 2 }}>
              You will be redirected to the admin dashboard in a moment...
            </Alert>
            <Button 
              variant="contained" 
              color="primary" 
              sx={{ mt: 3 }}
              onClick={() => navigate('/admin')}
            >
              Go to Dashboard Now
            </Button>
          </Box>
        )}

        {status === 'error' && (
          <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', my: 3 }}>
            <Security color="error" sx={{ fontSize: 48, mb: 2 }} />
            <Typography variant="body1" gutterBottom>Login bypass failed</Typography>
            <Alert severity="error" sx={{ width: '100%', mt: 2 }}>
              {error || 'An unknown error occurred during login bypass'}
            </Alert>
            <Button 
              variant="outlined" 
              color="primary" 
              sx={{ mt: 3 }}
              onClick={bypassLogin}
            >
              Try Again
            </Button>
          </Box>
        )}
      </Paper>
    </Container>
  );
};

export default LoginBypass;
