import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1e4d8c',      // Deeper blue as primary color
      light: '#3a75c4',    // Lighter blue
      dark: '#0d2c5b',     // Darker blue
      contrastText: '#ffffff',
    },
    secondary: {
      main: '#00a0b2',      // Teal as secondary color
      light: '#4dd0e1',    // Lighter teal
      dark: '#007380',     // Darker teal
      contrastText: '#ffffff',
    },
    error: {
      main: '#d32f2f',
    },
    background: {
      default: '#f0f4fa',   // Light blue-gray background
      paper: '#ffffff',
    },
    text: {
      primary: '#2c3e50',   // Dark blue-gray text
      secondary: '#546e7a', // Medium blue-gray text
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    h1: {
      fontSize: '2.5rem',
      fontWeight: 500,
      color: '#1e4d8c',
    },
    h2: {
      fontSize: '2rem',
      fontWeight: 500,
