    '0 24px 48px rgba(0,0,0,0.36)',
    '0 26px 52px rgba(0,0,0,0.39)',
    '0 28px 56px rgba(0,0,0,0.42)',
    '0 30px 60px rgba(0,0,0,0.45)',
    '0 32px 64px rgba(0,0,0,0.48)',
    '0 34px 68px rgba(0,0,0,0.51)',
