import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  TextField,
  Button,
  Box,
  Alert,
  Grid,
  MenuItem,
  FormControl,
  FormLabel,
  RadioGroup,
  FormControlLabel,
  Radio
} from '@mui/material';
import axios from 'axios';

const priorities = ['low', 'medium', 'high', 'urgent'];
const softwareProducts = ['kaspersky', 'sophos', 'forti', 'other'];
const hardwareProducts = ['laptop', 'desktop', 'server', 'network', 'printer', 'other'];

// Backend API URL
const API_URL = 'http://localhost:5002';

const CreateTicket = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const isNewUser = location.state?.isNewUser;
  const [formData, setFormData] = useState({
    category: 'software',
    productType: 'kaspersky',
    productName: '',
    serialNumber: '',
    warrantyStatus: true,
    warrantyExpiry: '',
    numberOfUsers: '',
    priority: 'medium',
    description: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });

    // Reset product type when category changes
    if (name === 'category') {
      setFormData(prev => ({
        ...prev,
        [name]: value,
        productType: value === 'software' ? 'kaspersky' : 'laptop'
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    // Validate description length
    if (formData.description.length > 500) {
      setError('Description is too long. Please limit to 500 characters.');
      setLoading(false);
      return;
    }

    try {
      // Convert numberOfUsers to a number
      const ticketData = {
        ...formData,
        numberOfUsers: parseInt(formData.numberOfUsers, 10) || 1
      };

      console.log('Sending ticket data to:', `${API_URL}/api/tickets`);
      console.log('Ticket data:', ticketData);

      const response = await axios.post(
        `${API_URL}/api/tickets`,
        ticketData,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        }
      );

      navigate('/client/tickets/' + response.data._id);
    } catch (err) {
      console.error('Ticket creation error:', err);
      const errorMessage = err.response?.data?.message || 'Failed to create ticket. Please try again.';
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ mt: 4, mb: 4 }}>
        {isNewUser && (
          <Alert severity="success" sx={{ mb: 3 }}>
            Welcome! Thank you for registering. Let's create your first support ticket.
          </Alert>
        )}

        <Paper sx={{ p: 4 }} elevation={3}>
          <Typography variant="h5" gutterBottom>
            Create Support Ticket
          </Typography>

          {error && (
            <Alert severity="error" sx={{ mb: 3 }}>
              {error}
            </Alert>
          )}

          <form onSubmit={handleSubmit}>
            <Grid container spacing={3}>
              <Grid item xs={12}>
                <FormControl component="fieldset">
                  <FormLabel component="legend">Issue Category</FormLabel>
                  <RadioGroup
                    row
                    name="category"
                    value={formData.category}
                    onChange={handleChange}
                  >
                    <FormControlLabel
                      value="software"
                      control={<Radio />}
                      label="Software"
                    />
                    <FormControlLabel
                      value="hardware"
                      control={<Radio />}
                      label="Hardware"
                    />
                  </RadioGroup>
                </FormControl>
              </Grid>

              {formData.category === 'software' && (
                <>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      select
                      fullWidth
                      name="productType"
                      label="Software Type"
                      value={formData.productType}
                      onChange={handleChange}
                      required
                    >
                      {softwareProducts.map((product) => (
                        <MenuItem key={product} value={product}>
                          {product.charAt(0).toUpperCase() + product.slice(1)}
                        </MenuItem>
                      ))}
                    </TextField>
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      fullWidth
                      name="productName"
                      label="Product Name/Version"
                      value={formData.productName}
                      onChange={handleChange}
                      required
                      placeholder="e.g., Kaspersky Endpoint Security 11.7.0"
                    />
                  </Grid>
                </>
              )}

              {formData.category === 'hardware' && (
                <>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      select
                      fullWidth
                      name="productType"
                      label="Hardware Type"
                      value={formData.productType}
                      onChange={handleChange}
                      required
                    >
                      {hardwareProducts.map((product) => (
                        <MenuItem key={product} value={product}>
                          {product.charAt(0).toUpperCase() + product.slice(1)}
                        </MenuItem>
                      ))}
                    </TextField>
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      fullWidth
                      name="productName"
                      label="Product Model"
                      value={formData.productName}
                      onChange={handleChange}
                      required
                      placeholder="e.g., Dell Latitude 5420"
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      fullWidth
                      name="serialNumber"
                      label="Serial Number"
                      value={formData.serialNumber}
                      onChange={handleChange}
                      placeholder="Optional"
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <FormControl component="fieldset">
                      <FormLabel component="legend">Under Warranty?</FormLabel>
                      <RadioGroup
                        row
                        name="warrantyStatus"
                        value={formData.warrantyStatus.toString()}
                        onChange={(e) => setFormData({
                          ...formData,
                          warrantyStatus: e.target.value === 'true'
                        })}
                      >
                        <FormControlLabel
                          value="true"
                          control={<Radio />}
                          label="Yes"
                        />
                        <FormControlLabel
                          value="false"
                          control={<Radio />}
                          label="No"
                        />
                      </RadioGroup>
                    </FormControl>
                  </Grid>
                </>
              )}

              <Grid item xs={12}>
                <TextField
                  fullWidth
                  name="numberOfUsers"
                  label="Number of Users Affected"
                  type="number"
                  value={formData.numberOfUsers}
                  onChange={handleChange}
                  required
                  inputProps={{ min: 1 }}
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  select
                  fullWidth
                  name="priority"
                  label="Priority"
                  value={formData.priority}
                  onChange={handleChange}
                  required
                >
                  {priorities.map((priority) => (
                    <MenuItem key={priority} value={priority}>
                      {priority.charAt(0).toUpperCase() + priority.slice(1)}
                    </MenuItem>
                  ))}
                </TextField>
              </Grid>

              <Grid item xs={12}>
                <TextField
                  fullWidth
                  multiline
                  rows={4}
                  name="description"
                  label="Description"
                  value={formData.description}
                  onChange={handleChange}
                  required
                  inputProps={{ maxLength: 500 }}
                  helperText={`Please provide detailed information about the issue (${formData.description.length}/500 characters)`}
                />
              </Grid>
            </Grid>

            <Box sx={{ mt: 3, display: 'flex', justifyContent: 'space-between' }}>
              <Button
                variant="outlined"
                onClick={() => navigate('/client/tickets')}
              >
                Cancel
              </Button>
              <Button
                type="submit"
                variant="contained"
                disabled={loading}
              >
                {loading ? 'Creating...' : 'Create Ticket'}
              </Button>
            </Box>
          </form>
        </Paper>
      </Box>
    </Container>
  );
};

export default CreateTicket;
