import io from 'socket.io-client';
let socket = null;
let socketListeners = [];

/**
 * Get or initialize the socket connection
 * @returns {Socket} The socket instance
 */
export const getSocket = () => {
  if (!socket) {
    // Create new socket connection
    const socketUrl = process.env.REACT_APP_SOCKET_URL || 'http://localhost:5002';
    socket = io(socketUrl);
    console.log('Socket connection established to:', socketUrl);

    // Apply any registered listeners
    socketListeners.forEach(({
      event,
      callback
    }) => {
      socket.on(event, callback);
    });
  }
  return socket;
};

/**
 * Register an event listener that persists across reconnections
 * @param {string} event - The event name
 * @param {function} callback - The callback function
 */
export const onSocketEvent = (event, callback) => {
  // Store the listener for reconnections
  socketListeners.push({
    event,
    callback
  });

  // If socket exists, add the listener immediately
  if (socket) {
    socket.on(event, callback);
  }
};

/**
 * Remove a specific event listener
 * @param {string} event - The event name
 * @param {function} callback - The callback function to remove
 */
export const offSocketEvent = (event, callback) => {
  // Remove from our tracking array
  socketListeners = socketListeners.filter(listener => !(listener.event === event && listener.callback === callback));

  // Remove from socket if it exists
  if (socket) {
    socket.off(event, callback);
  }
};

/**
 * Close the socket connection
 */
export const closeSocket = () => {
  if (socket) {
    socket.disconnect();
    socket = null;
    socketListeners = [];
    console.log('Socket connection closed');
  }
};

