import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { Box, Container, Grid, Paper, Typography, Tab, Tabs, CircularProgress } from '@mui/material';
import { useTickets } from '../../contexts/TicketContext';
import { useAuth } from '../../contexts/AuthContext';
import TicketList from '../tickets/TicketList';
import StatsCard from '../../components/dashboard/StatsCard';
import axios from 'axios';
import { API_URL } from '../../utils/apiConfig';

const ClientDashboard = () => {
  const location = useLocation();
  const { user } = useAuth();
  const { tickets, fetchTickets } = useTickets();
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    totalTickets: 0,
    activeTickets: 0,
    resolvedTickets: 0
  });
  const [selectedTab, setSelectedTab] = useState(0);

  useEffect(() => {
    // Check if we need to refresh based on navigation state
    if (location.state?.refresh) {
      // Clear the state to prevent infinite refreshes
      window.history.replaceState({}, document.title);
      // Refresh the dashboard
      refreshDashboard();
    }

    // Fetch tickets using the context
    fetchTickets().then(() => {
      fetchStats();
      setLoading(false);
    }).catch(error => {
      console.error('Error fetching tickets:', error);
      setLoading(false);
    });
  }, [fetchTickets, tickets, location.state]); // Add tickets to dependency array

  // Separate function to fetch just the stats
  const fetchStats = async () => {
    try {
      const statsRes = await axios.get(`${API_URL}/tickets/stats`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });

      console.log(statesRes.data)

      setStats(statsRes.data);

      // Add this fallback in case the API doesn't return updated stats
      if (!statsRes.data || Object.keys(statsRes.data).length === 0) {
        // Calculate stats from tickets array as fallback
        const activeTickets = tickets.filter(t => t.status !== 'Resolved').length;
        const resolvedTickets = tickets.filter(t => t.status === 'Resolved').length;

        setStats({
          totalTickets: tickets.length,
          activeTickets: activeTickets,
          resolvedTickets: resolvedTickets
        });
      }
    } catch (error) {
      console.error('Error fetching stats:', error.response?.data || error.message);

      // Calculate stats from tickets array as fallback
      const activeTickets = tickets.filter(t => t.status !== 'Resolved').length;
      const resolvedTickets = tickets.filter(t => t.status === 'Resolved').length;

      setStats({
        totalTickets: tickets.length,
        activeTickets: activeTickets,
        resolvedTickets: resolvedTickets
      });
    }
  };

  // Add this function
  const refreshDashboard = () => {
    fetchTickets().then(() => {
      fetchStats();
    }).catch(error => {
      console.error('Error refreshing dashboard:', error);
    });
  };

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
  };

  // Filter tickets based on selected tab
  const getFilteredTickets = () => {
    if (selectedTab === 0) {
      return tickets; // All tickets
    } else if (selectedTab === 1) {
      return tickets.filter(ticket => ticket.status !== 'Resolved'); // Active tickets
    } else {
      return tickets.filter(ticket => ticket.status === 'Resolved'); // Resolved tickets
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container maxWidth="lg">
      <Box my={4}>
        <Typography variant="h4" component="h1" gutterBottom>
          Welcome, {user?.firstName}!
        </Typography>
        <Typography variant="body1" color="textSecondary" paragraph>
          This is your support dashboard. Here you can create new support tickets and view the status of your existing tickets.
        </Typography>

        <Grid container spacing={3} sx={{ mb: 4 }}>
          <Grid item xs={12} sm={4}>
            <StatsCard 
              title="Total Tickets" 
              value={stats.totalTickets} 
              color="#e3f2fd" 
            />
          </Grid>
          <Grid item xs={12} sm={4}>
            <StatsCard 
              title="Active Tickets" 
              value={stats.activeTickets} 
              color="#fff8e1" 
            />
          </Grid>
          <Grid item xs={12} sm={4}>
            <StatsCard 
              title="Resolved Tickets" 
              value={stats.resolvedTickets} 
              color="#e8f5e9" 
            />
          </Grid>
        </Grid>

        <Paper elevation={2} sx={{ mb: 3 }}>
          <Tabs 
            value={selectedTab} 
            onChange={handleTabChange} 
            indicatorColor="primary"
            textColor="primary"
            variant="fullWidth"
          >
            <Tab label="All Tickets" />
            <Tab label="Active Tickets" />
            <Tab label="Resolved Tickets" />
          </Tabs>
        </Paper>

        <TicketList 
          tickets={getFilteredTickets()} 
          showCreateButton={true} 
          emptyMessage="No tickets found. Create a new support ticket to get help."
        />
      </Box>
    </Container>
  );
};

export default ClientDashboard;
