} from '@mui/material';
import {
  Assignment as TicketIcon,
  People as PeopleIcon,
  Warning as UrgentIcon,
  CheckCircle as ResolvedIcon,
  History as HistoryIcon,
  SupervisorAccount as ClientsIcon,
  Dashboard as DashboardIcon
} from '@mui/icons-material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import TicketList from '../../components/tickets/TicketList';

const StatCard = ({ title, value, icon, color }) => (
  <Card sx={{ height: '100%' }}>
    <CardContent>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
        {React.cloneElement(icon, { sx: { color, mr: 1 } })}
        <Typography color="textSecondary" variant="h6">
          {title}
        </Typography>
      </Box>
      <Typography variant="h4">{value}</Typography>
    </CardContent>
  </Card>
);

const ActionCard = ({ title, description, icon, color, onClick }) => (
  <Card 
    sx={{ 
      height: '100%', 
      cursor: 'pointer', 
      transition: 'transform 0.2s, box-shadow 0.2s',
      '&:hover': {
        transform: 'translateY(-4px)',
        boxShadow: 6
      } 
    }}
    onClick={onClick}
  >
    <CardContent>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
        {React.cloneElement(icon, { sx: { color, fontSize: 28, mr: 1 } })}
        <Typography variant="h6" fontWeight="bold">
          {title}
        </Typography>
      </Box>
      <Typography variant="body2" color="text.secondary">
        {description}
      </Typography>
    </CardContent>
  </Card>
);

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    totalTickets: 0,
    totalUsers: 0,
    urgentTickets: 0,
    resolvedToday: 0
  });
  const [tickets, setTickets] = useState([]);
  const [technicians, setTechnicians] = useState([]);
  const [selectedTab, setSelectedTab] = useState(0);
  const [assignDialogOpen, setAssignDialogOpen] = useState(false);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [selectedTechnician, setSelectedTechnician] = useState('');
  const [assignAllDialogOpen, setAssignAllDialogOpen] = useState(false);
  const [assignAllTechnician, setAssignAllTechnician] = useState('');

  useEffect(() => {
    fetchDashboardData();
    fetchTechnicians();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [statsRes, ticketsRes] = await Promise.all([
        axios.get(`${process.env.REACT_APP_API_URL}/api/admin/stats`),
        axios.get(`${process.env.REACT_APP_API_URL}/api/admin/tickets`)
      ]);

      setStats(statsRes.data);
      setTickets(ticketsRes.data);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    }
  };

  const fetchTechnicians = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/users?role=technical`);
      setTechnicians(response.data);
    } catch (error) {
      console.error('Error fetching technicians:', error);
    }
  };

  const handleAssignTicket = (ticketId) => {
    setSelectedTicket(ticketId);
    setAssignDialogOpen(true);
  };

  const handleConfirmAssign = async () => {
    try {
      await axios.post(`${process.env.REACT_APP_API_URL}/api/admin/tickets/${selectedTicket}/assign`, {
        technicianId: selectedTechnician
      });
      fetchDashboardData();
      setAssignDialogOpen(false);
      setSelectedTicket(null);
      setSelectedTechnician('');
    } catch (error) {
      console.error('Error assigning ticket:', error);
    }
  };

  const assignAllUnassignedTickets = async (technicianUsername) => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/users/find?username=${technicianUsername}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });

      const technician = response.data;
      if (!technician || !technician._id) {
        alert(`Technician ${technicianUsername} not found`);
        return;
      }

      const unassignedTickets = tickets.filter(ticket => !ticket.assignedTo);
      console.log(`Found ${unassignedTickets.length} unassigned tickets to assign to ${technicianUsername}`);

      if (unassignedTickets.length === 0) {
        alert('No unassigned tickets found');
        return;
      }

      const assignPromises = unassignedTickets.map(ticket => 
        axios.patch(
          `${process.env.REACT_APP_API_URL}/api/tickets/${ticket._id}/assign`,
          { technicianId: technician._id },
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem('token')}`,
              'Content-Type': 'application/json'
            }
          }
        )
      );

      await Promise.all(assignPromises);
      alert(`Successfully assigned ${unassignedTickets.length} tickets to ${technicianUsername}`);

      fetchDashboardData();
    } catch (error) {
      console.error('Error assigning all unassigned tickets:', error);
      alert(`Failed to assign tickets: ${error.message}`);
    }
  };

  const filterTickets = () => {
    switch (selectedTab) {
      case 0: // All
        return tickets;
      case 1: // Unassigned
        return tickets.filter(ticket => !ticket.assignedTo);
      case 2: // Urgent
        return tickets.filter(ticket => ticket.priority === 'urgent');
      case 3: // Resolved
        return tickets.filter(ticket => ticket.status === 'resolved');
      default:
        return tickets;
    }
  };

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom color="primary" fontWeight="bold">
        Admin Dashboard
      </Typography>
      <Typography variant="body1" color="text.secondary" paragraph>
        Monitor system performance and manage support tickets.
      </Typography>
      <Divider sx={{ mb: 4 }} />

      <Grid container spacing={3}>
        {/* Stats Cards */}
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Total Tickets"
            value={stats.totalTickets}
            icon={<TicketIcon />}
            color="primary.main"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Total Users"
            value={stats.totalUsers}
            icon={<PeopleIcon />}
            color="secondary.main"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Urgent Tickets"
            value={stats.urgentTickets}
            icon={<UrgentIcon />}
            color="error.main"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Resolved Today"
            value={stats.resolvedToday}
            icon={<ResolvedIcon />}
            color="success.main"
          />
        </Grid>

        {/* Action Cards */}
        <Grid item xs={12}>
          <Typography variant="h5" sx={{ mt: 2, mb: 2 }}>
            Quick Actions
          </Typography>
        </Grid>

        <Grid item xs={12} sm={6} md={4}>
          <ActionCard
            title="Client Management"
            description="View and manage all registered clients in the system"
            icon={<ClientsIcon />}
            color="primary.main"
            onClick={() => navigate('/admin/clients')}
          />
        </Grid>

        <Grid item xs={12} sm={6} md={4}>
          <ActionCard
            title="Ticket History"
            description="Access complete ticket history and analytics"
            icon={<HistoryIcon />}
            color="secondary.main"
            onClick={() => navigate('/admin/tickets/history')}
          />
        </Grid>

        <Grid item xs={12} sm={6} md={4}>
          <ActionCard
            title="System Dashboard"
            description="View system performance metrics and statistics"
            icon={<DashboardIcon />}
            color="info.main"
            onClick={() => {}}
          />
        </Grid>

        <Grid item xs={12} sm={6} md={4}>
          <ActionCard
            title="Assign All Unassigned Tickets"
            description="Assign all unassigned tickets to a specific technician"
            icon={<TicketIcon />}
            color="primary.main"
            onClick={() => setAssignAllDialogOpen(true)}
          />
        </Grid>

        {/* Tickets Section */}
        <Grid item xs={12}>
          <Paper sx={{ p: 3, mt: 3 }}>
            <Typography variant="h5" gutterBottom fontWeight="bold">
              Recent Tickets
            </Typography>
            <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 2 }}>
              <Tabs value={selectedTab} onChange={(e, newValue) => setSelectedTab(newValue)}>
                <Tab label="All Tickets" />
                <Tab label="Unassigned" />
                <Tab label="Urgent" />
                <Tab label="Resolved" />
              </Tabs>
            </Box>
            <TicketList
              tickets={filterTickets()}
              onAssign={handleAssignTicket}
              showAssignButton={selectedTab === 1}
              showClientInfo={true}
            />

            <Box sx={{ mt: 2, display: 'flex', justifyContent: 'flex-end' }}>
              <Button 
                variant="outlined" 
                color="primary"
                onClick={() => navigate('/admin/tickets')}
              >
                View All Tickets
              </Button>
            </Box>
          </Paper>
        </Grid>
      </Grid>

      {/* Assign Ticket Dialog */}
      <Dialog open={assignDialogOpen} onClose={() => setAssignDialogOpen(false)}>
        <DialogTitle>Assign Ticket</DialogTitle>
        <DialogContent>
          <FormControl fullWidth sx={{ mt: 2 }}>
            <InputLabel>Select Technician</InputLabel>
            <Select
              value={selectedTechnician}
              onChange={(e) => setSelectedTechnician(e.target.value)}
              label="Select Technician"
            >
              {technicians.map((tech) => (
                <MenuItem key={tech._id} value={tech._id}>
                  {tech.name} - {tech.specialization?.join(', ') || 'General'}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setAssignDialogOpen(false)}>Cancel</Button>
          <Button
            onClick={handleConfirmAssign}
            variant="contained"
            disabled={!selectedTechnician}
          >
            Assign
          </Button>
        </DialogActions>
      </Dialog>

      {/* Assign All Unassigned Tickets Dialog */}
      <Dialog open={assignAllDialogOpen} onClose={() => setAssignAllDialogOpen(false)}>
        <DialogTitle>Assign All Unassigned Tickets</DialogTitle>
        <DialogContent>
          <FormControl fullWidth sx={{ mt: 2 }}>
            <InputLabel>Select Technician</InputLabel>
            <Select
              value={assignAllTechnician}
              onChange={(e) => setAssignAllTechnician(e.target.value)}
              label="Select Technician"
            >
              {technicians.map((tech) => (
                <MenuItem key={tech._id} value={tech.username}>
                  {tech.name} - {tech.specialization?.join(', ') || 'General'}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setAssignAllDialogOpen(false)}>Cancel</Button>
          <Button
            onClick={() => assignAllUnassignedTickets(assignAllTechnician)}
            variant="contained"
            disabled={!assignAllTechnician}
          >
            Assign
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default AdminDashboard;
