import React, { useState, useEffect } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Typography,
  CircularProgress,
  Alert,
  Box
} from '@mui/material';
import { useTickets } from '../../contexts/TicketContext';

const AssignTicketDialog = ({ open, handleClose, ticketId, onSuccess }) => {
  const [selectedTechnician, setSelectedTechnician] = useState('');
  const [error, setError] = useState(null);
  const { assignTicket, fetchTechnicians, technicians, technicianLoading } = useTickets();

  // Fetch technicians when dialog opens
  useEffect(() => {
    if (open) {
      console.log('Dialog opened, fetching technicians');
      fetchTechnicians();
      setSelectedTechnician('');
      setError(null);
    }
  }, [open, fetchTechnicians]);

  const handleAssign = async () => {
    if (!selectedTechnician) {
      setError('Please select a technician');
      return;
    }

    try {
      console.log(`Assigning ticket ${ticketId} to technician ${selectedTechnician}`);

      // Call assignTicket from TicketContext
      const result = await assignTicket(ticketId, selectedTechnician);
      console.log('Assignment result:', result);

      // Find the selected technician's details
      const selectedTech = technicians.find(tech => tech._id === selectedTechnician);
      console.log('Selected technician details:', selectedTech);

      if (onSuccess) {
        console.log('Calling onSuccess callback with result');
        onSuccess({
          ...result,
          assignedTo: selectedTech // Ensure we pass complete technician data
        });
      }
      handleClose();
    } catch (err) {
      console.error('Error assigning ticket:', err);
      console.error('Error details:', err.response?.data);
      setError(err.message || 'Failed to assign ticket');
    }
  };

  return (
    <Dialog open={open} onClose={handleClose} fullWidth maxWidth="sm">
      <DialogTitle>Assign Ticket</DialogTitle>
      <DialogContent>
        {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

        <Typography variant="body2" sx={{ mb: 2 }}>
          Select a technician to assign this ticket to:
        </Typography>

        {technicianLoading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', padding: '20px' }}>
            <CircularProgress size={30} />
          </Box>
        ) : (
          <FormControl fullWidth error={!technicians.length}>
            <InputLabel id="technician-select-label">Technician</InputLabel>
            <Select
              labelId="technician-select-label"
              value={selectedTechnician}
              onChange={(e) => setSelectedTechnician(e.target.value)}
              label="Technician"
              disabled={technicianLoading}
            >
              {technicians.length === 0 ? (
                <MenuItem disabled value="">
                  No technicians available
                </MenuItem>
              ) : (
                technicians.map((tech) => (
                  <MenuItem key={tech._id} value={tech._id}>
                    {tech.username} ({tech.firstName} {tech.lastName})
                  </MenuItem>
                ))
              )}
            </Select>
          </FormControl>
        )}
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose} disabled={technicianLoading}>Cancel</Button>
        <Button 
          onClick={handleAssign} 
          color="primary" 
          variant="contained" 
          disabled={technicianLoading || !selectedTechnician}
        >
          {technicianLoading ? <CircularProgress size={24} /> : 'Assign'}
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default AssignTicketDialog;
