import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  TextField,
  Button,
  Box,
  Alert,
  Grid,
  MenuItem,
  FormControl,
  FormLabel,
  RadioGroup,
  FormControlLabel,
  Radio
} from '@mui/material';
import axios from 'axios';

const priorities = ['low', 'medium', 'high', 'urgent'];
const softwareProducts = ['Kaspersky', 'Sophos', 'Forti', 'Barracuda'];

const CreateTicket = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const isNewUser = location.state?.isNewUser;
  const [formData, setFormData] = useState({
    type: 'software',
    product: '',
    numberOfUsers: '',
    warrantyStatus: 'yes',
    description: '',
    priority: 'medium'
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/tickets`,
        formData,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        }
      );

      navigate('/client/tickets/' + response.data._id);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to create ticket');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ mt: 4, mb: 4 }}>
        {isNewUser && (
          <Alert severity="success" sx={{ mb: 3 }}>
            Welcome! Thank you for registering. Let's create your first support ticket.
          </Alert>
        )}

        <Paper sx={{ p: 4 }}>
          <Typography variant="h5" gutterBottom>
            Create Support Ticket
          </Typography>

          {error && (
            <Alert severity="error" sx={{ mb: 3 }}>
              {error}
            </Alert>
          )}

          <form onSubmit={handleSubmit}>
            <Grid container spacing={3}>
              <Grid item xs={12}>
                <FormControl component="fieldset">
                  <FormLabel component="legend">Issue Type</FormLabel>
                  <RadioGroup
                    row
                    name="type"
                    value={formData.type}
                    onChange={handleChange}
                  >
                    <FormControlLabel
                      value="software"
                      control={<Radio />}
                      label="Software"
                    />
                    <FormControlLabel
                      value="hardware"
                      control={<Radio />}
                      label="Hardware"
                    />
                  </RadioGroup>
                </FormControl>
              </Grid>

              {formData.type === 'software' && (
                <Grid item xs={12}>
                  <TextField
                    select
                    fullWidth
                    name="product"
                    label="Software Product"
                    value={formData.product}
                    onChange={handleChange}
                    required
                  >
                    {softwareProducts.map((product) => (
                      <MenuItem key={product} value={product}>
                        {product}
                      </MenuItem>
                    ))}
                  </TextField>
                </Grid>
              )}

              <Grid item xs={12}>
                <TextField
                  fullWidth
                  name="numberOfUsers"
                  label="Number of Users Affected"
                  type="number"
                  value={formData.numberOfUsers}
                  onChange={handleChange}
                  required
                />
              </Grid>

              {formData.type === 'hardware' && (
                <Grid item xs={12}>
                  <FormControl component="fieldset">
                    <FormLabel component="legend">Under Warranty?</FormLabel>
                    <RadioGroup
                      row
                      name="warrantyStatus"
                      value={formData.warrantyStatus}
                      onChange={handleChange}
                    >
                      <FormControlLabel
                        value="yes"
                        control={<Radio />}
                        label="Yes"
                      />
                      <FormControlLabel
                        value="no"
                        control={<Radio />}
                        label="No"
                      />
                      <FormControlLabel
                        value="unknown"
                        control={<Radio />}
                        label="Unknown"
                      />
                    </RadioGroup>
                  </FormControl>
                </Grid>
              )}

              <Grid item xs={12}>
                <TextField
                  select
                  fullWidth
                  name="priority"
                  label="Priority"
                  value={formData.priority}
                  onChange={handleChange}
                  required
                >
                  {priorities.map((priority) => (
                    <MenuItem key={priority} value={priority}>
                      {priority.charAt(0).toUpperCase() + priority.slice(1)}
                    </MenuItem>
                  ))}
                </TextField>
              </Grid>

              <Grid item xs={12}>
                <TextField
                  fullWidth
                  multiline
                  rows={4}
                  name="description"
                  label="Description"
                  value={formData.description}
                  onChange={handleChange}
                  required
                  helperText="Please provide detailed information about the issue"
                />
              </Grid>
            </Grid>

            <Box sx={{ mt: 3, display: 'flex', justifyContent: 'space-between' }}>
              <Button
                variant="outlined"
                onClick={() => navigate('/client/tickets')}
              >
                Cancel
              </Button>
              <Button
                type="submit"
                variant="contained"
                disabled={loading}
              >
                {loading ? 'Creating...' : 'Create Ticket'}
              </Button>
            </Box>
          </form>
        </Paper>
      </Box>
    </Container>
  );
};

export default CreateTicket;
