import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Paper,
  TextField,
  Button,
  Typography,
  Box,
  Alert,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Grid,
  Divider
} from '@mui/material';
import { useAuth } from '../../contexts/AuthContext';

const Register = () => {
  const navigate = useNavigate();
  const { register, user } = useAuth();
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    firstName: '',
    lastName: '',
    username: '',
    phoneNumber: '',
    role: 'client',
    companyName: '',
    department: 'IT',
    specialization: []
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Redirect if user is not an admin but tries to register a technician
  useEffect(() => {
    // Allow non-technical accounts by default unless the user is an admin
    const isAdminUser = user && user.role === 'admin';

    // If user tries to create a technical account but is not an admin, reset to client
    if (formData.role === 'technical' && !isAdminUser) {
      setFormData(prev => ({ ...prev, role: 'client' }));
      setError('Only administrators can create technical accounts');
    }
  }, [formData.role, user]);

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === 'specialization') {
      setFormData({
        ...formData,
        [name]: typeof value === 'string' ? value.split(',') : value
      });
    } else {
      setFormData({
        ...formData,
        [name]: value
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (formData.password !== formData.confirmPassword) {
      return setError('Passwords do not match');
    }

    // If trying to create a technical account without being an admin
    if (formData.role === 'technical' && (!user || user.role !== 'admin')) {
      return setError('Only administrators can create technical accounts');
    }

    setLoading(true);

    try {
      const userData = {
        email: formData.email,
        password: formData.password,
        firstName: formData.firstName,
        lastName: formData.lastName,
        username: formData.username,
        phoneNumber: formData.phoneNumber,
        role: formData.role,
        ...(formData.role === 'client' && {
          companyName: formData.companyName,
          department: formData.department
        }),
        ...(formData.role === 'technical' && {
          specialization: formData.specialization
        })
      };

      console.log('Submitting user data:', userData);
      const newUser = await register(userData);
      console.log('Registration successful:', newUser);

      // Redirect based on user role
      switch (newUser.role) {
        case 'client':
          navigate('/client');
          break;
        case 'technical':
          navigate('/technical');
          break;
        default:
          navigate('/login');
      }
    } catch (err) {
      console.error('Registration error:', err);
      setError(err.response?.data?.message || 'Failed to create account');
    } finally {
      setLoading(false);
    }
  };

  const departments = [
    'IT', 'Finance', 'Human Resources', 'Marketing', 'Sales', 
    'Operations', 'Research & Development', 'Customer Service', 'Legal', 'Other'
  ];

  const specializations = [
    'Hardware', 'Software', 'Network', 'Cloud Services', 'Database', 
    'Security', 'Cybersecurity', 'Firewall', 'Mobile Devices', 'Printers', 'Email', 'VoIP'
  ];

  // Only show admin options when logged in as admin
  const isAdmin = user && user.role === 'admin';

  return (
    <Container component="main" maxWidth="sm">
      <Box
        sx={{
          marginTop: 8,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        <Paper
          elevation={3}
          sx={{
            padding: 4,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            width: '100%',
          }}
        >
          <Typography component="h1" variant="h5">
            {isAdmin ? 'Create New User Account' : 'Create Client Account'}
          </Typography>

          {error && (
            <Alert severity="error" sx={{ mt: 2, width: '100%' }}>
              {error}
            </Alert>
          )}

          <Box component="form" onSubmit={handleSubmit} sx={{ mt: 3, width: '100%' }}>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  id="firstName"
                  label="First Name"
                  name="firstName"
                  value={formData.firstName}
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  id="lastName"
                  label="Last Name"
                  name="lastName"
                  value={formData.lastName}
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="username"
                  label="Username"
                  name="username"
                  autoComplete="username"
                  value={formData.username}
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="email"
                  label="Email Address"
                  name="email"
                  autoComplete="email"
                  value={formData.email}
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="phoneNumber"
                  label="Phone Number"
                  name="phoneNumber"
                  value={formData.phoneNumber}
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="password"
                  label="Password"
                  type="password"
                  id="password"
                  autoComplete="new-password"
                  value={formData.password}
                  onChange={handleChange}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="confirmPassword"
                  label="Confirm Password"
                  type="password"
                  id="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                />
              </Grid>

              {/* Only show role selection if logged in as admin */}
              {isAdmin && (
                <Grid item xs={12}>
                  <FormControl fullWidth>
                    <InputLabel id="role-label">Account Type</InputLabel>
                    <Select
                      labelId="role-label"
                      id="role"
                      name="role"
                      value={formData.role}
                      label="Account Type"
                      onChange={handleChange}
                    >
                      <MenuItem value="client">Client</MenuItem>
                      <MenuItem value="technical">Technician</MenuItem>
                      <MenuItem value="admin">Administrator</MenuItem>
                    </Select>
                  </FormControl>
                </Grid>
              )}

              {/* Fields for client account type */}
              {(formData.role === 'client') && (
                <>
                  <Grid item xs={12}>
                    <TextField
                      required
                      fullWidth
                      id="companyName"
                      label="Company Name"
                      name="companyName"
                      value={formData.companyName}
                      onChange={handleChange}
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <FormControl fullWidth>
                      <InputLabel id="department-label">Department</InputLabel>
                      <Select
                        labelId="department-label"
                        id="department"
                        name="department"
                        value={formData.department}
                        label="Department"
                        onChange={handleChange}
                      >
                        {departments.map((dept) => (
                          <MenuItem key={dept} value={dept}>{dept}</MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                </>
              )}

              {/* Fields for technical account type (only shown if admin creates it) */}
              {(formData.role === 'technical' && isAdmin) && (
                <Grid item xs={12}>
                  <FormControl fullWidth>
                    <InputLabel id="specialization-label">Specialization</InputLabel>
                    <Select
                      labelId="specialization-label"
                      id="specialization"
                      name="specialization"
                      multiple
                      value={formData.specialization}
                      label="Specialization"
                      onChange={handleChange}
                    >
                      {specializations.map((spec) => (
                        <MenuItem key={spec} value={spec}>{spec}</MenuItem>
                      ))}
                    </Select>
                  </FormControl>
                </Grid>
              )}
            </Grid>

            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
              disabled={loading}
            >
              {loading ? 'Creating Account...' : 'Create Account'}
            </Button>

            <Button
              fullWidth
              variant="outlined"
              onClick={() => navigate('/login')}
            >
              Back to Login
            </Button>
          </Box>
        </Paper>
      </Box>
    </Container>
  );
};

export default Register;
