import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import {
  Container,
  Paper,
  TextField,
  Button,
  Typography,
  Box,
  Alert,
  Grid,
  CircularProgress,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
  FormHelperText
} from '@mui/material';
import { PersonAddOutlined } from '@mui/icons-material';
import { useAuth } from '../../contexts/AuthContext';
import axios from 'axios';
import { API_URL } from '../../utils/apiConfig';

const Register = () => {
  const navigate = useNavigate();
  const { register } = useAuth();
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    username: '',
    password: '',
    confirmPassword: '',
    companyName: '',
    department: '',
    phoneNumber: ''
  });
  const [error, setError] = useState('');
  const [debugInfo, setDebugInfo] = useState('');
  const [loading, setLoading] = useState(false);

  // Department options as requested
  const departmentOptions = [
    { value: 'Sales', label: 'Sales' },
    { value: 'IT', label: 'IT' },
    { value: 'HR', label: 'HR' },
    { value: 'Purchasing', label: 'Purchasing' }
  ];

  // Check API connection on component mount
  useEffect(() => {
    const checkApiConnection = async () => {
      try {
        setDebugInfo('Checking API connection...');
        const response = await axios.get(`${API_URL}/health-check`);
        setDebugInfo(prev => prev + '\nAPI health check response: ' + JSON.stringify(response.data));
      } catch (error) {
        setDebugInfo(prev => prev + '\nAPI connection error: ' + (error.response?.data?.message || error.message));
        // Don't show this error to users, just for debugging
      }
    };

    checkApiConnection();
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Direct registration attempt for debugging
  const directRegisterAttempt = async (userData) => {
    try {
      setDebugInfo(prev => prev + '\nAttempting direct axios registration...');
      const registerUrl = `${API_URL}/auth/register`;
      setDebugInfo(prev => prev + `\nRegistration URL: ${registerUrl}`);

      const response = await axios.post(registerUrl, userData);
      setDebugInfo(prev => prev + '\nDirect registration response: ' + JSON.stringify(response.data));
      return response.data;
    } catch (error) {
      const errorMsg = error.response?.data?.message || error.message;
      setDebugInfo(prev => prev + '\nDirect registration error: ' + errorMsg);
      if (error.response) {
        setDebugInfo(prev => prev + '\nError status: ' + error.response.status);
        setDebugInfo(prev => prev + '\nError details: ' + JSON.stringify(error.response.data));
      }
      throw error;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setDebugInfo('Starting registration process');

    // Validate form data
    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters');
      return;
    }

    setLoading(true);

    try {
      setDebugInfo(prev => prev + `\nForm data: ${JSON.stringify({...formData, password: '[HIDDEN]', confirmPassword: '[HIDDEN]'})}`);      

      // Try direct registration first to debug
      setDebugInfo(prev => prev + '\nAttempting direct registration');
      const userData = {
        ...formData,
        role: 'client' // Always register as client through this form
      };

      delete userData.confirmPassword; // Remove confirmPassword as it's not needed for API

      // Try direct registration first for debugging
      try {
        await directRegisterAttempt(userData);
        setDebugInfo(prev => prev + '\nDirect registration successful, proceeding with context registration');
      } catch (directError) {
        setDebugInfo(prev => prev + '\nDirect registration failed, but still trying context registration');
      }

      // Regular registration through context
      await register(userData);
      setDebugInfo(prev => prev + '\nRegistration successful, redirecting to login');
      navigate('/auth/login');
    } catch (error) {
      console.error('Registration error:', error.response?.data || error.message);
      setDebugInfo(prev => prev + '\nRegistration error: ' + (error.response?.data?.message || error.message));
      setError(error.response?.data?.message || 'Failed to register. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="md" sx={{ pt: 8 }}>
      <Paper elevation={3} sx={{ p: 4 }}>
        <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', mb: 4 }}>
          <PersonAddOutlined fontSize="large" sx={{ mb: 2 }} />
          <Typography variant="h5" component="h1" gutterBottom>
            Client Registration
          </Typography>
          <Typography variant="body2" color="text.secondary" align="center">
            Create a new account to submit and track support tickets
          </Typography>
        </Box>

        {error && <Alert severity="error" sx={{ mb: 3 }}>{error}</Alert>}

        {/* Debug information section - visible only in development */}
        {debugInfo && process.env.NODE_ENV !== 'production' && (
          <Alert severity="info" sx={{ mb: 3, whiteSpace: 'pre-wrap' }}>
            <Typography variant="subtitle2">Debug Information:</Typography>
            {debugInfo}
          </Alert>
        )}

        <Box component="form" onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <TextField
                required
                fullWidth
                label="First Name"
                name="firstName"
                value={formData.firstName}
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                required
                fullWidth
                label="Last Name"
                name="lastName"
                value={formData.lastName}
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                required
                fullWidth
                label="Email Address"
                name="email"
                type="email"
                value={formData.email}
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                required
                fullWidth
                label="Username"
                name="username"
                value={formData.username}
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                required
                fullWidth
                label="Password"
                name="password"
                type="password"
                value={formData.password}
                onChange={handleChange}
                helperText="Must be at least 6 characters"
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                required
                fullWidth
                label="Confirm Password"
                name="confirmPassword"
                type="password"
                value={formData.confirmPassword}
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Company Name"
                name="companyName"
                value={formData.companyName}
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth required>
                <InputLabel id="department-label">Department</InputLabel>
                <Select
                  labelId="department-label"
                  id="department"
                  name="department"
                  value={formData.department}
                  onChange={handleChange}
                  label="Department"
                >
                  {departmentOptions.map((option) => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))}
                </Select>
                <FormHelperText>Please select your department</FormHelperText>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Phone Number"
                name="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleChange}
              />
            </Grid>
          </Grid>
          <Button
            type="submit"
            fullWidth
            variant="contained"
            sx={{ mt: 3, mb: 2 }}
            disabled={loading}
          >
            {loading ? <CircularProgress size={24} /> : 'Register'}
          </Button>
          <Box sx={{ textAlign: 'center' }}>
            <Typography variant="body2">
              Already have an account?{' '}
              <Link to="/auth/login" style={{ textDecoration: 'none' }}>
                Sign in
              </Link>
            </Typography>
          </Box>
        </Box>
      </Paper>
    </Container>
  );
};

export default Register;
