import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  Box,
  Chip,
  Divider,
  TextField,
  Button,
  CircularProgress,
  Alert,
  Grid,
  Card,
  CardContent,
  List,
  ListItem,
  ListItemText,
  ListItemAvatar,
  Avatar
} from '@mui/material';
import axios from 'axios';
import { useAuth } from '../../contexts/AuthContext';
import {
  joinTicketRoom,
  leaveTicketRoom,
  sendTicketMessage,
  onSocketEvent,
  offSocketEvent
} from '../../services/socketService';
import { API_URL } from '../../services/apiConfig';

// Status color mapping
const statusColors = {
  new: 'info',
  in_progress: 'warning',
  on_hold: 'error',
  resolved: 'success',
  closed: 'default'
};

// Priority color mapping
const priorityColors = {
  low: 'success',
  medium: 'info',
  high: 'warning',
  urgent: 'error'
};

const TicketDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [ticket, setTicket] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [messageSending, setMessageSending] = useState(false);

  // Fetch ticket details
  useEffect(() => {
    const fetchTicket = async () => {
      try {
        const response = await axios.get(`${API_URL}/api/tickets/${id}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        });
        setTicket(response.data);
      } catch (err) {
        console.error('Error fetching ticket:', err);
        setError('Failed to load ticket details. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    const fetchMessages = async () => {
      try {
        const response = await axios.get(`${API_URL}/api/tickets/${id}/messages`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        });
        setMessages(response.data);
      } catch (err) {
        console.error('Error fetching messages:', err);
      }
    };

    fetchTicket();
    fetchMessages();

    // Join ticket room for real-time updates
    joinTicketRoom(id);

    // Listen for new messages
    const handleNewMessage = (message) => {
      setMessages(prevMessages => [...prevMessages, message]);
    };

    // Listen for ticket updates
    const handleTicketUpdate = (updatedTicket) => {
      setTicket(updatedTicket);
    };

    onSocketEvent('new_message', handleNewMessage);
    onSocketEvent('ticket_updated', handleTicketUpdate);

    return () => {
      // Clean up socket listeners and leave room
      offSocketEvent('new_message', handleNewMessage);
      offSocketEvent('ticket_updated', handleTicketUpdate);
      leaveTicketRoom(id);
    };
  }, [id]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;

    setMessageSending(true);
    try {
      await sendTicketMessage({
        ticketId: id,
        content: newMessage
      });
      setNewMessage('');
    } catch (err) {
      console.error('Error sending message:', err);
      setError('Failed to send message. Please try again.');
    } finally {
      setMessageSending(false);
    }
  };

  const handleStatusChange = async (newStatus) => {
    try {
      await axios.patch(
        `${API_URL}/api/tickets/${id}/status`,
        { status: newStatus },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        }
      );
    } catch (err) {
      console.error('Error updating ticket status:', err);
      setError('Failed to update ticket status. Please try again.');
    }
  };

  const handleAssignTicket = async () => {
    try {
      await axios.patch(
        `${API_URL}/api/tickets/${id}/assign`,
        { technicianId: user.userId },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        }
      );
    } catch (err) {
      console.error('Error assigning ticket:', err);
      setError('Failed to assign ticket. Please try again.');
    }
  };

  if (loading) {
    return (
      <Container maxWidth="md">
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
          <CircularProgress />
        </Box>
      </Container>
    );
  }

  if (error) {
    return (
      <Container maxWidth="md">
        <Box sx={{ mt: 4 }}>
          <Alert severity="error">{error}</Alert>
          <Button
            variant="outlined"
            onClick={() => navigate(-1)}
            sx={{ mt: 2 }}
          >
            Go Back
          </Button>
        </Box>
      </Container>
    );
  }

  if (!ticket) {
    return (
      <Container maxWidth="md">
        <Box sx={{ mt: 4 }}>
          <Alert severity="warning">Ticket not found</Alert>
          <Button
            variant="outlined"
            onClick={() => navigate(-1)}
            sx={{ mt: 2 }}
          >
            Go Back
          </Button>
        </Box>
      </Container>
    );
  }

  return (
    <Container maxWidth="md">
      <Box sx={{ mt: 4, mb: 4 }}>
        <Paper sx={{ p: 3 }} elevation={3}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
            <Typography variant="h5">
              Ticket #{ticket.ticketNumber}
            </Typography>
            <Chip
              label={ticket.status.replace('_', ' ').toUpperCase()}
              color={statusColors[ticket.status]}
            />
          </Box>

          <Divider sx={{ mb: 3 }} />

          <Grid container spacing={3}>
            <Grid item xs={12} md={8}>
              <Typography variant="h6" gutterBottom>
                {ticket.product.name}
              </Typography>
              <Typography variant="body1" paragraph>
                {ticket.description}
              </Typography>
            </Grid>

            <Grid item xs={12} md={4}>
              <Card variant="outlined">
                <CardContent>
                  <Typography variant="subtitle2" color="text.secondary" gutterBottom>
                    Details
                  </Typography>

                  <Box sx={{ mb: 1 }}>
                    <Typography variant="body2" color="text.secondary">
                      Category
                    </Typography>
                    <Typography variant="body1">
                      {ticket.category.charAt(0).toUpperCase() + ticket.category.slice(1)}
                    </Typography>
                  </Box>

                  <Box sx={{ mb: 1 }}>
                    <Typography variant="body2" color="text.secondary">
                      Product Type
                    </Typography>
                    <Typography variant="body1">
                      {ticket.product.type ? 
                        (ticket.product.type.charAt(0).toUpperCase() + ticket.product.type.slice(1)) : 
                        'N/A'}
                    </Typography>
                  </Box>

                  <Box sx={{ mb: 1 }}>
                    <Typography variant="body2" color="text.secondary">
                      Priority
                    </Typography>
                    <Chip
                      size="small"
                      label={ticket.priority.toUpperCase()}
                      color={priorityColors[ticket.priority]}
                    />
                  </Box>

                  <Box sx={{ mb: 1 }}>
                    <Typography variant="body2" color="text.secondary">
                      Created
                    </Typography>
                    <Typography variant="body1">
                      {new Date(ticket.createdAt).toLocaleString()}
                    </Typography>
                  </Box>

                  <Box sx={{ mb: 1 }}>
                    <Typography variant="body2" color="text.secondary">
                      Users Affected
                    </Typography>
                    <Typography variant="body1">
                      {ticket.numberOfUsers || 1}
                    </Typography>
                  </Box>

                  {ticket.assignedTo ? (
                    <Box sx={{ mb: 1 }}>
                      <Typography variant="body2" color="text.secondary">
                        Assigned To
                      </Typography>
                      <Typography variant="body1">
                        {ticket.assignedTo.name}
                      </Typography>
                    </Box>
                  ) : (
                    user.role === 'technician' && (
                      <Button
                        variant="outlined"
                        size="small"
                        onClick={handleAssignTicket}
                        sx={{ mt: 1 }}
                      >
                        Assign to Me
                      </Button>
                    )
                  )}
                </CardContent>
              </Card>

              {(user.role === 'admin' || user.role === 'technician') && (
                <Box sx={{ mt: 2 }}>
                  <Typography variant="subtitle2" gutterBottom>
                    Update Status
                  </Typography>
                  <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
                    <Button
                      size="small"
                      variant="outlined"
                      color="primary"
                      onClick={() => handleStatusChange('in_progress')}
                      disabled={ticket.status === 'in_progress'}
                    >
                      In Progress
                    </Button>
                    <Button
                      size="small"
                      variant="outlined"
                      color="warning"
                      onClick={() => handleStatusChange('on_hold')}
                      disabled={ticket.status === 'on_hold'}
                    >
                      On Hold
                    </Button>
                    <Button
                      size="small"
                      variant="outlined"
                      color="success"
                      onClick={() => handleStatusChange('resolved')}
                      disabled={ticket.status === 'resolved'}
                    >
                      Resolved
                    </Button>
                    <Button
                      size="small"
                      variant="outlined"
                      color="error"
                      onClick={() => handleStatusChange('closed')}
                      disabled={ticket.status === 'closed'}
                    >
                      Close
                    </Button>
                  </Box>
                </Box>
              )}
            </Grid>
          </Grid>

          <Divider sx={{ my: 3 }} />

          <Typography variant="h6" gutterBottom>
            Messages
          </Typography>

          <List>
            {messages.length > 0 ? (
              messages.map((message, index) => (
                <ListItem key={index} alignItems="flex-start" sx={{ px: 0 }}>
                  <ListItemAvatar>
                    <Avatar>{message.sender.name.charAt(0)}</Avatar>
                  </ListItemAvatar>
                  <ListItemText
                    primary={
                      <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                        <Typography variant="subtitle2">
                          {message.sender.name}
                        </Typography>
                        <Typography variant="caption" color="text.secondary">
                          {new Date(message.timestamp).toLocaleString()}
                        </Typography>
                      </Box>
                    }
                    secondary={message.content}
                  />
                </ListItem>
              ))
            ) : (
              <Typography variant="body2" color="text.secondary">
                No messages yet.
              </Typography>
            )}
          </List>

          <Box component="form" onSubmit={handleSendMessage} sx={{ mt: 3 }}>
            <TextField
              fullWidth
              multiline
              rows={2}
              placeholder="Type your message here..."
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              disabled={messageSending}
              inputProps={{ maxLength: 500 }}
              helperText={`${newMessage.length}/500 characters`}
            />
            <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 1 }}>
              <Button
                type="submit"
                variant="contained"
                disabled={!newMessage.trim() || messageSending}
              >
                {messageSending ? 'Sending...' : 'Send Message'}
              </Button>
            </Box>
          </Box>
        </Paper>
      </Box>
    </Container>
  );
};

export default TicketDetails;
