import React, { createContext, useContext, useState, useCallback } from 'react';
import axios from 'axios';
import { TICKET_ENDPOINTS } from '../utils/apiConfig';

const TicketContext = createContext();

export const useTickets = () => {
  const context = useContext(TicketContext);
  if (!context) {
    throw new Error('useTickets must be used within a TicketProvider');
  }
  return context;
};

export const TicketProvider = ({ children }) => {
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch all tickets
  const fetchTickets = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(TICKET_ENDPOINTS.GET_ALL, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });
      setTickets(response.data);
      console.log('Tickets fetched:', response.data);
      return response.data;
    } catch (err) {
      console.error('Error fetching tickets:', err);
      setError(err.message || 'Failed to fetch tickets');
      return [];
    } finally {
      setLoading(false);
    }
  }, []);

  // Add a new ticket to the local state
  const addTicket = useCallback((newTicket) => {
    console.log('Adding ticket to context:', newTicket);
    setTickets(prevTickets => [newTicket, ...prevTickets]);
  }, []);

  // Create a new ticket
  const createTicket = useCallback(async (ticketData) => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.post(
        TICKET_ENDPOINTS.CREATE,
        ticketData,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
            'Content-Type': 'application/json'
          }
        }
      );

      // If successful, add the new ticket to our local state
      if (response.data.ticket) {
        addTicket(response.data.ticket);
      }

      return response.data;
    } catch (err) {
      console.error('Error creating ticket:', err);
      setError(err.message || 'Failed to create ticket');
      throw err;
    } finally {
      setLoading(false);
    }
  }, [addTicket]);

  const value = {
    tickets,
    loading,
    error,
    fetchTickets,
    addTicket,
    createTicket
  };

  return <TicketContext.Provider value={value}>{children}</TicketContext.Provider>;
};
