import React, { useState, useEffect } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Typography,
  CircularProgress,
  Alert
} from '@mui/material';
import axios from 'axios';
import { API_URL } from '../../utils/apiConfig';
import { useTickets } from '../../contexts/TicketContext';

const AssignTicketDialog = ({ open, handleClose, ticketId, onSuccess }) => {
  const [technicians, setTechnicians] = useState([]);
  const [selectedTechnician, setSelectedTechnician] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const { assignTicket } = useTickets();

  useEffect(() => {
    const fetchTechnicians = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`${API_URL}/users/technicians`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        });
        setTechnicians(response.data);
      } catch (err) {
        console.error('Error fetching technicians:', err);
        setError('Failed to load technicians');
      } finally {
        setLoading(false);
      }
    };

    if (open) {
      fetchTechnicians();
      setSelectedTechnician('');
      setError(null);
    }
  }, [open]);

  const handleAssign = async () => {
    if (!selectedTechnician) {
      setError('Please select a technician');
      return;
    }

    try {
      setLoading(true);
      await assignTicket(ticketId, selectedTechnician);
      onSuccess?.(); // Call success callback if provided
      handleClose();
    } catch (err) {
      console.error('Error assigning ticket:', err);
      setError(err.message || 'Failed to assign ticket');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Dialog open={open} onClose={handleClose} fullWidth maxWidth="sm">
      <DialogTitle>Assign Ticket</DialogTitle>
      <DialogContent>
        {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

        <Typography variant="body2" sx={{ mb: 2 }}>
          Select a technician to assign this ticket to:
        </Typography>

        {loading && technicians.length === 0 ? (
          <div style={{ display: 'flex', justifyContent: 'center', padding: '20px' }}>
            <CircularProgress size={30} />
          </div>
        ) : (
          <FormControl fullWidth>
            <InputLabel id="technician-select-label">Technician</InputLabel>
            <Select
              labelId="technician-select-label"
              value={selectedTechnician}
              onChange={(e) => setSelectedTechnician(e.target.value)}
              label="Technician"
              disabled={loading}
            >
              {technicians.map((tech) => (
                <MenuItem key={tech._id} value={tech._id}>
                  {tech.firstName && tech.lastName 
                    ? `${tech.firstName} ${tech.lastName}` 
                    : tech.username}
                </MenuItem>
              ))}
              {technicians.length === 0 && (
                <MenuItem disabled value="">
                  No technicians available
                </MenuItem>
              )}
            </Select>
          </FormControl>
        )}
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose} disabled={loading}>Cancel</Button>
        <Button 
          onClick={handleAssign} 
          variant="contained" 
          color="primary" 
          disabled={loading || !selectedTechnician}
        >
          {loading ? <CircularProgress size={24} /> : 'Assign'}
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default AssignTicketDialog;
