import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Grid,
  Paper,
  Typography,
  Box,
  Card,
  CardContent,
  Button,
  Tab,
  Tabs,
  CircularProgress,
  Fab,
  Snackbar,
  Alert
} from '@mui/material';
import axios from 'axios';
import AddIcon from '@mui/icons-material/Add';
import { green, blue, red, orange, grey } from '@mui/material/colors';
import { useAuth } from '../../contexts/AuthContext';
import { useTickets } from '../../contexts/TicketContext';
import TicketList from '../../components/tickets/TicketList';
import { API_URL } from '../../utils/apiConfig';

const ClientDashboard = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const { tickets, loading: ticketsLoading, fetchTickets } = useTickets();
  const [stats, setStats] = useState({
    totalTickets: 0,
    activeTickets: 0,
    resolvedTickets: 0
  });
  const [selectedTab, setSelectedTab] = useState(0);
  const [loading, setLoading] = useState(true);
  const [notification, setNotification] = useState({ open: false, message: '', severity: 'info' });

  useEffect(() => {
    // Fetch tickets using the context
    fetchTickets().then(() => {
      fetchStats();
      setLoading(false);
    }).catch(error => {
      console.error('Error fetching tickets:', error);
      setLoading(false);
    });
  }, [fetchTickets]);

  // Separate function to fetch just the stats
  const fetchStats = async () => {
    try {
      const statsRes = await axios.get(`${API_URL}/tickets/stats`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });

      setStats(statsRes.data);
    } catch (error) {
      console.error('Error fetching stats:', error.response?.data || error.message);
    }
  };

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
  };

  const handleCreateTicket = () => {
    navigate('/client/tickets/create');
  };

  const handleCloseNotification = () => {
    setNotification({ ...notification, open: false });
  };

  return (
    <Box sx={{ flexGrow: 1, p: 3 }}>
      <Grid container spacing={3}>
        {/* Header with welcome message */}
        <Grid item xs={12}>
          <Paper sx={{ p: 3, bgcolor: '#f9f9f9' }}>
            <Typography variant="h4" component="h1" gutterBottom>
              Welcome, {user?.firstName || user?.name || user?.username}!
            </Typography>
            <Typography variant="body1">
              This is your support dashboard. Here you can create new support tickets and view the status of your existing tickets.
            </Typography>
          </Paper>
        </Grid>

        {/* Stats cards */}
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent sx={{ bgcolor: blue[50], textAlign: 'center' }}>
              <Typography variant="h5" component="div" gutterBottom>
                Total Tickets
              </Typography>
              <Typography variant="h2">
                {stats.totalTickets || 0}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent sx={{ bgcolor: orange[50], textAlign: 'center' }}>
              <Typography variant="h5" component="div" gutterBottom>
                Active Tickets
              </Typography>
              <Typography variant="h2">
                {stats.activeTickets || 0}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent sx={{ bgcolor: green[50], textAlign: 'center' }}>
              <Typography variant="h5" component="div" gutterBottom>
                Resolved Tickets
              </Typography>
              <Typography variant="h2">
                {stats.resolvedTickets || 0}
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        {/* Tickets List with Tabs */}
        <Grid item xs={12}>
          <Paper sx={{ p: 2 }}>
            <Box sx={{ borderBottom: 1, borderColor: 'divider', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Tabs value={selectedTab} onChange={handleTabChange} aria-label="ticket status tabs">
                <Tab label="All Tickets" />
                <Tab label="Active Tickets" />
                <Tab label="Resolved Tickets" />
              </Tabs>
              <Button
                variant="contained"
                color="primary"
                startIcon={<AddIcon />}
                onClick={handleCreateTicket}
                sx={{ my: 1 }}
              >
                Create New Ticket
              </Button>
            </Box>

            {/* Show loading indicator if tickets are still loading */}
            {loading || ticketsLoading ? (
              <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
                <CircularProgress />
              </Box>
            ) : (
              <Box sx={{ mt: 2 }}>
                {selectedTab === 0 && (
                  <TicketList 
                    tickets={tickets}
                    showAssign={false}
                    emptyMessage="You haven't created any tickets yet."
                    highlightProductInfo={true}
                  />
                )}
                {selectedTab === 1 && (
                  <TicketList 
                    tickets={tickets.filter(ticket => 
                      !['resolved', 'closed'].includes(ticket.status)
                    )}
                    showAssign={false}
                    emptyMessage="You don't have any active tickets."
                    highlightProductInfo={true}
                  />
                )}
                {selectedTab === 2 && (
                  <TicketList 
                    tickets={tickets.filter(ticket => 
                      ['resolved', 'closed'].includes(ticket.status)
                    )}
                    showAssign={false}
                    emptyMessage="You don't have any resolved tickets."
                    highlightProductInfo={true}
                  />
                )}
              </Box>
            )}
          </Paper>
        </Grid>

        {/* Create ticket floating action button for mobile */}
        <Fab 
          color="primary" 
          aria-label="create ticket" 
          sx={{ position: 'fixed', bottom: 16, right: 16 }}
          onClick={handleCreateTicket}
        >
          <AddIcon />
        </Fab>

        {/* Notification snackbar */}
        <Snackbar 
          open={notification.open} 
          autoHideDuration={6000} 
          onClose={handleCloseNotification}
          anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
        >
          <Alert onClose={handleCloseNotification} severity={notification.severity}>
            {notification.message}
          </Alert>
        </Snackbar>
      </Grid>
    </Box>
  );
};

export default ClientDashboard;
