import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  Box,
  Button,
  Container,
  FormControl,
  FormHelperText,
  Grid,
  InputLabel,
  MenuItem,
  Paper,
  Select,
  TextField,
  Typography,
  Alert,
  Snackbar,
  CircularProgress,
  Checkbox,
  FormControlLabel,
  Stack
} from '@mui/material';
import { useAuth } from '../../contexts/AuthContext';
import { TICKET_ENDPOINTS } from '../../utils/apiConfig';

const CreateTicket = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [createdTicketId, setCreatedTicketId] = useState(null);

  const [formData, setFormData] = useState({
    category: '',
    productType: '',
    productName: '',
    serialNumber: '',
    warrantyStatus: false,
    warrantyExpiry: '',
    priority: 'medium',
    description: '',
    numberOfUsers: 1
  });

  const [validation, setValidation] = useState({
    description: {
      error: false,
      message: ''
    }
  });

  // Category options
  const categories = [
    'Hardware',
    'Software',
    'Network',
    'Account',
    'Other'
  ];

  // Product type options based on category
  const productTypes = {
    Hardware: ['Laptop', 'Desktop', 'Printer', 'Server', 'Mobile Device', 'Other'],
    Software: ['Operating System', 'Application', 'Database', 'Security', 'Other'],
    Network: ['Internet', 'Intranet', 'VPN', 'Email', 'Other'],
    Account: ['Password Reset', 'Access Request', 'Permission Issue', 'Other'],
    Other: ['General Inquiry', 'Training Request', 'Feedback', 'Other']
  };

  // Priority options
  const priorities = [
    { value: 'low', label: 'Low' },
    { value: 'medium', label: 'Medium' },
    { value: 'high', label: 'High' },
    { value: 'urgent', label: 'Urgent' },
    { value: 'critical', label: 'Critical' }
  ];

  // Handle input changes
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    // Handle checkbox inputs
    if (type === 'checkbox') {
      setFormData({
        ...formData,
        [name]: checked
      });
      return;
    }

    // Reset product type when category changes
    if (name === 'category') {
      setFormData({
        ...formData,
        [name]: value,
        productType: ''
      });
      return;
    }

    // Validate description length
    if (name === 'description') {
      if (value.length > 1000) {
        setValidation({
          ...validation,
          description: {
            error: true,
            message: 'Description cannot exceed 1000 characters'
          }
        });
      } else {
        setValidation({
          ...validation,
          description: {
            error: false,
            message: ''
          }
        });
      }
    }

    setFormData({
      ...formData,
      [name]: value
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Basic validation
    if (!formData.category) {
      setError('Please select a category');
      return;
    }

    if (!formData.description) {
      setError('Please provide a description');
      return;
    }

    if (validation.description.error) {
      setError('Please fix the errors before submitting');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      console.log('Submitting ticket with data:', formData);

      const response = await axios.post(
        TICKET_ENDPOINTS.CREATE,
        formData,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
            'Content-Type': 'application/json'
          }
        }
      );

      console.log('Ticket creation response:', response.data);

      setSuccess(true);
      setCreatedTicketId(response.data.ticket._id);

      // Redirect to ticket details after a short delay
      setTimeout(() => {
        navigate(`/tickets/${response.data.ticket._id}`);
      }, 1500);

    } catch (err) {
      console.error('Error creating ticket:', err);
      const errorMessage = err.response?.data?.message || 'An error occurred while creating the ticket';
      setError(errorMessage);

      // Log additional details for debugging
      if (err.response) {
        console.error('Error response data:', err.response.data);
        console.error('Error response status:', err.response.status);
      } else if (err.request) {
        console.error('No response received from server');
      } else {
        console.error('Error setting up request:', err.message);
      }
    } finally {
      setLoading(false);
    }
  };

  // Handle snackbar close
  const handleCloseSnackbar = () => {
    setError(null);
    setSuccess(false);
  };

  // Handle view ticket button click
  const handleViewTicket = () => {
    if (createdTicketId) {
      navigate(`/tickets/${createdTicketId}`);
    }
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} sx={{ p: 4, mt: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Create Support Ticket
        </Typography>

        <Box component="form" onSubmit={handleSubmit} noValidate>
          <Grid container spacing={3}>
            {/* Category Selection */}
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth required>
                <InputLabel id="category-label">Category</InputLabel>
                <Select
                  labelId="category-label"
                  id="category"
                  name="category"
                  value={formData.category}
                  onChange={handleChange}
                  label="Category"
                >
                  {categories.map((category) => (
                    <MenuItem key={category} value={category}>
                      {category}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>

            {/* Product Type Selection (conditional) */}
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth disabled={!formData.category}>
                <InputLabel id="productType-label">Product Type</InputLabel>
                <Select
                  labelId="productType-label"
                  id="productType"
                  name="productType"
                  value={formData.productType}
                  onChange={handleChange}
                  label="Product Type"
                >
                  {formData.category && productTypes[formData.category]?.map((type) => (
                    <MenuItem key={type} value={type}>
                      {type}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>

            {/* Product Name */}
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                id="productName"
                name="productName"
                label="Product Name"
                value={formData.productName}
                onChange={handleChange}
                placeholder={formData.category ? `Enter ${formData.category.toLowerCase()} name` : 'Enter product name'}
              />
            </Grid>

            {/* Serial Number (optional) */}
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                id="serialNumber"
                name="serialNumber"
                label="Serial Number (optional)"
                value={formData.serialNumber}
                onChange={handleChange}
              />
            </Grid>

            {/* Warranty Status */}
            <Grid item xs={12} sm={6}>
              <FormControlLabel
                control={
                  <Checkbox
                    checked={formData.warrantyStatus}
                    onChange={handleChange}
                    name="warrantyStatus"
                  />
                }
                label="Product is under warranty"
              />
            </Grid>

            {/* Warranty Expiry (conditional) */}
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                id="warrantyExpiry"
                name="warrantyExpiry"
                label="Warranty Expiry Date"
                type="date"
                value={formData.warrantyExpiry}
                onChange={handleChange}
                disabled={!formData.warrantyStatus}
                InputLabelProps={{ shrink: true }}
              />
            </Grid>

            {/* Number of Users Affected */}
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                id="numberOfUsers"
                name="numberOfUsers"
                label="Number of Users Affected"
                type="number"
                value={formData.numberOfUsers}
                onChange={handleChange}
                inputProps={{ min: 1 }}
              />
            </Grid>

            {/* Priority Selection */}
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth required>
                <InputLabel id="priority-label">Priority</InputLabel>
                <Select
                  labelId="priority-label"
                  id="priority"
                  name="priority"
                  value={formData.priority}
                  onChange={handleChange}
                  label="Priority"
                >
                  {priorities.map((priority) => (
                    <MenuItem key={priority.value} value={priority.value}>
                      {priority.label}
                    </MenuItem>
                  ))}
                </Select>
                <FormHelperText>
                  Select the appropriate priority level for your issue
                </FormHelperText>
              </FormControl>
            </Grid>

            {/* Description */}
            <Grid item xs={12}>
              <TextField
                fullWidth
                id="description"
                name="description"
                label="Description"
                multiline
                rows={4}
                value={formData.description}
                onChange={handleChange}
                required
                error={validation.description.error}
                helperText={validation.description.error ? validation.description.message : `${formData.description.length}/1000 characters`}
                inputProps={{ maxLength: 1000 }}
              />
            </Grid>

            {/* Submit Button */}
            <Grid item xs={12}>
              <Box sx={{ display: 'flex', justifyContent: 'flex-end' }}>
                <Button
                  type="submit"
                  variant="contained"
                  color="primary"
                  disabled={loading}
                  sx={{ mt: 2 }}
                >
                  {loading ? <CircularProgress size={24} /> : 'Submit Ticket'}
                </Button>
              </Box>
            </Grid>
          </Grid>
        </Box>
      </Paper>

      {/* Error Snackbar */}
      <Snackbar open={!!error} autoHideDuration={6000} onClose={handleCloseSnackbar}>
        <Alert onClose={handleCloseSnackbar} severity="error" sx={{ width: '100%' }}>
          {error}
        </Alert>
      </Snackbar>

      {/* Success Snackbar */}
      <Snackbar 
        open={success} 
        autoHideDuration={6000} 
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert 
          onClose={handleCloseSnackbar} 
          severity="success" 
          sx={{ width: '100%' }}
          action={
            <Button color="inherit" size="small" onClick={handleViewTicket}>
              VIEW TICKET
            </Button>
          }
        >
          Ticket created successfully! Redirecting to ticket details...
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default CreateTicket;
