import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  Box,
  Button,
  Container,
  FormControl,
  FormHelperText,
  Grid,
  InputLabel,
  MenuItem,
  Paper,
  Select,
  TextField,
  Typography,
  Alert,
  Snackbar,
  CircularProgress,
  Checkbox,
  FormControlLabel,
  Stack
} from '@mui/material';
import { useAuth } from '../../contexts/AuthContext';
import { TICKET_ENDPOINTS } from '../../utils/apiConfig';

const CreateTicket = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [createdTicketId, setCreatedTicketId] = useState(null);
  const [createdTicketNumber, setCreatedTicketNumber] = useState(null);
  const [connectionStatus, setConnectionStatus] = useState(null);

  const [formData, setFormData] = useState({
    category: '',
    productType: '',
    productName: '',
    serialNumber: '',
    warrantyStatus: false,
    warrantyExpiry: '',
    priority: 'medium',
    description: '',
    numberOfUsers: 1
  });

  const [validation, setValidation] = useState({
    category: {
      error: false,
      message: ''
    },
    description: {
      error: false,
      message: ''
    },
    priority: {
      error: false,
      message: ''
    }
  });

  // Category options
  const categories = [
    'Hardware',
    'Software',
    'Network',
    'Account',
    'Other'
  ];

  // Product type options based on category
  const productTypes = {
    Hardware: ['Laptop', 'Desktop', 'Printer', 'Server', 'Mobile Device', 'Other'],
    Software: ['Kaspersky', 'Sophos', 'Fortinet', 'Microsoft', 'Barracuda', 'Operating System', 'Application', 'Database', 'Security', 'Other'],
    Network: ['Internet', 'Intranet', 'VPN', 'Email', 'Other'],
    Account: ['Password Reset', 'Access Request', 'Permission Issue', 'Other'],
    Other: ['General Inquiry', 'Training Request', 'Feedback', 'Other']
  };

  // Priority options
  const priorities = [
    { value: 'low', label: 'Low' },
    { value: 'medium', label: 'Medium' },
    { value: 'high', label: 'High' },
    { value: 'urgent', label: 'Urgent' },
    { value: 'critical', label: 'Critical' }
  ];

  // Validate form fields
  const validateForm = () => {
    let isValid = true;
    const newValidation = { ...validation };

    // Validate category
    if (!formData.category) {
      newValidation.category = {
        error: true,
        message: 'Category is required'
      };
      isValid = false;
    } else {
      newValidation.category = {
        error: false,
        message: ''
      };
    }

    // Description is now optional, only validate length if provided
    if (formData.description && formData.description.length > 1000) {
      newValidation.description = {
        error: true,
        message: 'Description cannot exceed 1000 characters'
      };
      isValid = false;
    } else {
      newValidation.description = {
        error: false,
        message: ''
      };
    }

    // Validate priority
    if (!formData.priority) {
      newValidation.priority = {
        error: true,
        message: 'Priority is required'
      };
      isValid = false;
    } else {
      newValidation.priority = {
        error: false,
        message: ''
      };
    }

    setValidation(newValidation);
    return isValid;
  };

  // Handle input changes
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    // Handle checkbox inputs
    if (type === 'checkbox') {
      setFormData({
        ...formData,
        [name]: checked
      });
      return;
    }

    // Reset product type when category changes
    if (name === 'category') {
      setFormData({
        ...formData,
        [name]: value,
        productType: ''
      });

      // Clear category validation error when value is selected
      if (value) {
        setValidation({
          ...validation,
          category: {
            error: false,
            message: ''
          }
        });
      }
      return;
    }

    // Validate description length
    if (name === 'description') {
      if (value.length > 1000) {
        setValidation({
          ...validation,
          description: {
            error: true,
            message: 'Description cannot exceed 1000 characters'
          }
        });
      } else {
        setValidation({
          ...validation,
          description: {
            error: false,
            message: ''
          }
        });
      }
    }

    // Clear priority validation error when value is selected
    if (name === 'priority' && value) {
      setValidation({
        ...validation,
        priority: {
          error: false,
          message: ''
        }
      });
    }

    setFormData({
      ...formData,
      [name]: value
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate form before submission
    if (!validateForm()) {
      setError('Please fix the errors before submitting');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      // Log detailed information for debugging
      console.log('Form data being submitted:', formData);
      console.log('API endpoint:', TICKET_ENDPOINTS.CREATE);
      console.log('Using API URL:', process.env.REACT_APP_API_URL);

      // Ensure productName is set if not provided by user
      const submissionData = {
        ...formData,
        productName: formData.productName || formData.category || 'Unknown Product'
      };

      console.log('Submission data after preprocessing:', submissionData);
      console.log('Auth token present:', !!localStorage.getItem('token'));

      const response = await axios.post(
        TICKET_ENDPOINTS.CREATE,
        submissionData,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
            'Content-Type': 'application/json'
          },
          // Add timeout to prevent hanging requests
          timeout: 10000
        }
      );

      console.log('Ticket creation response:', response.data);

      if (response.data.success) {
        setSuccess(true);
        setCreatedTicketId(response.data.ticket._id);
        setCreatedTicketNumber(response.data.ticket.ticketNumber);

        // Clear form data on success
        setFormData({
          category: '',
          productType: '',
          productName: '',
          serialNumber: '',
          warrantyStatus: false,
          warrantyExpiry: '',
          priority: 'medium',
          description: '',
          numberOfUsers: 1
        });
      } else {
        // Handle unexpected success response format
        setError('Ticket created but with unexpected response format');
        console.warn('Unexpected response format:', response.data);
      }
    } catch (err) {
      console.error('Error creating ticket:', err);

      // Extract error message from response
      let errorMessage = 'An error occurred while creating the ticket';

      if (err.response) {
        console.error('Error response data:', err.response.data);
        console.error('Error response status:', err.response.status);

        if (err.response.data && err.response.data.message) {
          errorMessage = err.response.data.message;
        } else if (err.response.status === 400) {
          errorMessage = 'Invalid ticket data. Please check your inputs.';
        } else if (err.response.status === 401) {
          errorMessage = 'Authentication error. Please log in again.';
        } else if (err.response.status === 500) {
          errorMessage = 'Server error. Please try again later.';
        }
      } else if (err.request) {
        console.error('No response received from server');
        errorMessage = 'No response from server. Please check your connection.';
      } else {
        console.error('Error setting up request:', err.message);
      }

      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  // Check API connectivity when component mounts
  useEffect(() => {
    const checkConnection = async () => {
      try {
        setConnectionStatus('checking');

        // Log connection details
        console.log('Checking API connection to:', process.env.REACT_APP_API_URL);
        console.log('Auth token:', localStorage.getItem('token') ? 'Present' : 'Missing');

        // Attempt a simple request to verify connection
        const response = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/auth/verify`,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem('token')}`
            },
            timeout: 5000
          }
        );

        console.log('Connection check response:', response.data);
        setConnectionStatus('connected');
      } catch (err) {
        console.error('API connection check failed:', err);

        if (err.response) {
          console.log('Response status:', err.response.status);
          console.log('Response data:', err.response.data);

          // Auth error but connection works
          if (err.response.status === 401) {
            setConnectionStatus('auth_error');
          } else {
            setConnectionStatus('error');
          }
        } else if (err.request) {
          // No response received - likely connection issue
          console.log('No response received from server');
          setConnectionStatus('no_connection');
        } else {
          setConnectionStatus('unknown_error');
        }
      }
    };

    checkConnection();
  }, []);

  // Handle snackbar close
  const handleCloseSnackbar = () => {
    setError(null);
    setSuccess(false);
  };

  // Handle view ticket button click
  const handleViewTicket = () => {
    if (createdTicketId) {
      navigate(`/tickets/${createdTicketId}`);
    }
  };

  return (
    <Container maxWidth="md">
      {connectionStatus && connectionStatus !== 'connected' && (
        <Alert severity="warning" sx={{ mt: 2 }}>
          {connectionStatus === 'checking' && 'Checking connection to API server...'}
          {connectionStatus === 'no_connection' && 'Cannot connect to the API server. Please check if the server is running on port 5002.'}
          {connectionStatus === 'auth_error' && 'Connected to API server but authentication failed. Please log in again.'}
          {connectionStatus === 'error' && 'Error connecting to API server. Check console for details.'}
          {connectionStatus === 'unknown_error' && 'Unknown error occurred during API connection check.'}
        </Alert>
      )}

      <Paper elevation={3} sx={{ p: 4, mt: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Create Support Ticket
        </Typography>

        <Box component="form" onSubmit={handleSubmit} noValidate>
          <Grid container spacing={3}>
            {/* Category Selection */}
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth required error={validation.category.error}>
                <InputLabel id="category-label">Category</InputLabel>
                <Select
                  labelId="category-label"
                  id="category"
                  name="category"
                  value={formData.category}
                  onChange={handleChange}
                  label="Category"
                >
                  {categories.map((category) => (
                    <MenuItem key={category} value={category}>
                      {category}
                    </MenuItem>
                  ))}
                </Select>
                {validation.category.error && (
                  <FormHelperText error>{validation.category.message}</FormHelperText>
                )}
              </FormControl>
            </Grid>

            {/* Product Type Selection (conditional) */}
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth disabled={!formData.category}>
                <InputLabel id="productType-label">Product Type</InputLabel>
                <Select
                  labelId="productType-label"
                  id="productType"
                  name="productType"
                  value={formData.productType}
                  onChange={handleChange}
                  label="Product Type"
                >
                  {formData.category && productTypes[formData.category]?.map((type) => (
                    <MenuItem key={type} value={type}>
                      {type}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>

            {/* Product Name */}
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                id="productName"
                name="productName"
                label="Product Name"
                value={formData.productName}
                onChange={handleChange}
                placeholder={formData.category ? `Enter ${formData.category.toLowerCase()} name` : 'Enter product name'}
              />
            </Grid>

            {/* Serial Number (optional) */}
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                id="serialNumber"
                name="serialNumber"
                label="Serial Number (optional)"
                value={formData.serialNumber}
                onChange={handleChange}
              />
            </Grid>

            {/* Warranty Status */}
            <Grid item xs={12} sm={6}>
              <FormControlLabel
                control={
                  <Checkbox
                    checked={formData.warrantyStatus}
                    onChange={handleChange}
                    name="warrantyStatus"
                  />
                }
                label="Product is under warranty"
              />
            </Grid>

            {/* Warranty Expiry (conditional) */}
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                id="warrantyExpiry"
                name="warrantyExpiry"
                label="Warranty Expiry Date"
                type="date"
                value={formData.warrantyExpiry}
                onChange={handleChange}
                disabled={!formData.warrantyStatus}
                InputLabelProps={{ shrink: true }}
              />
            </Grid>

            {/* Number of Users Affected */}
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                id="numberOfUsers"
                name="numberOfUsers"
                label="Number of Users Affected"
                type="number"
                value={formData.numberOfUsers}
                onChange={handleChange}
                inputProps={{ min: 1 }}
              />
            </Grid>

            {/* Priority Selection */}
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth required error={validation.priority.error}>
                <InputLabel id="priority-label">Priority</InputLabel>
                <Select
                  labelId="priority-label"
                  id="priority"
                  name="priority"
                  value={formData.priority}
                  onChange={handleChange}
                  label="Priority"
                >
                  {priorities.map((priority) => (
                    <MenuItem key={priority.value} value={priority.value}>
                      {priority.label}
                    </MenuItem>
                  ))}
                </Select>
                <FormHelperText error={validation.priority.error}>
                  {validation.priority.error 
                    ? validation.priority.message 
                    : 'Select the appropriate priority level for your issue'}
                </FormHelperText>
              </FormControl>
            </Grid>

            {/* Description */}
            <Grid item xs={12}>
              <TextField
                fullWidth
                id="description"
                name="description"
                label="Description (optional)"
                multiline
                rows={4}
                value={formData.description}
                onChange={handleChange}
                error={validation.description.error}
                helperText={validation.description.error 
                  ? validation.description.message 
                  : `${formData.description.length}/1000 characters`}
                inputProps={{ maxLength: 1000 }}
              />
            </Grid>

            {/* Submit Button */}
            <Grid item xs={12}>
              <Box sx={{ display: 'flex', justifyContent: 'flex-end' }}>
                <Button
                  type="submit"
                  variant="contained"
                  color="primary"
                  disabled={loading}
                  sx={{ mt: 2 }}
                >
                  {loading ? <CircularProgress size={24} /> : 'Submit Ticket'}
                </Button>
              </Box>
            </Grid>
          </Grid>
        </Box>
      </Paper>

      {/* Error Snackbar */}
      <Snackbar open={!!error} autoHideDuration={6000} onClose={handleCloseSnackbar}>
        <Alert onClose={handleCloseSnackbar} severity="error" sx={{ width: '100%' }}>
          {error}
        </Alert>
      </Snackbar>

      {/* Success Snackbar */}
      <Snackbar 
        open={success} 
        autoHideDuration={6000} 
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert 
          onClose={handleCloseSnackbar} 
          severity="success" 
          sx={{ width: '100%' }}
          action={
            <Button color="inherit" size="small" onClick={handleViewTicket}>
              View Ticket
            </Button>
          }
        >
          {createdTicketNumber 
            ? `Ticket #${createdTicketNumber} created successfully!` 
            : 'Ticket created successfully!'}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default CreateTicket;
