import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { getSocket, closeSocket } from '../services/socketService';
import { API_URL } from '../services/apiConfig';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check if user is logged in on page load
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');

    if (token && userData) {
      const parsedUser = JSON.parse(userData);
      setUser(parsedUser);
      // Set default authorization header for all requests
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      // Initialize socket connection with auth token
      initializeSocket(token);
    }

    setLoading(false);
  }, []);

  // Initialize socket with authentication token
  const initializeSocket = (token) => {
    try {
      const socket = getSocket();
      // Set auth token for socket connection
      socket.auth = { token };
      // Connect if not already connected
      if (socket.disconnected) {
        socket.connect();
      }
    } catch (error) {
      console.error('Socket initialization error:', error);
    }
  };

  // Register a new user
  const register = async (userData) => {
    setLoading(true);
    try {
      console.log('Registering with data:', userData);
      const response = await axios.post(
        `${API_URL}/auth/register`,
        userData
      );

      const { user, token } = response.data;
      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(user));
      setUser(user);
      return user;
    } catch (error) {
      console.error('Registration error:', error.response?.data || error.message);
      throw error;
    } finally {
      setLoading(false);
    }
  };

  const login = async (email, password, userType = 'client') => {
    try {
      // Log login attempt (not credentials)
      console.log(`Attempting login for ${email} as ${userType}`);

      // Determine the endpoint based on user type
      const endpoint = userType === 'technician'
        ? `${API_URL}/auth/technician/login`
        : `${API_URL}/auth/client/login`;

      // Add additional logging to debug network issues
      console.log(`Sending request to: ${endpoint}`);

      // Use axios interceptor to log request details (only for debugging)
      const requestInterceptor = axios.interceptors.request.use(request => {
        console.log('Request details:', {
          url: request.url,
          method: request.method,
          headers: request.headers
        });
        return request;
      });

      const response = await axios.post(endpoint, {
        email,
        password // Password is sent as-is, no manual escaping needed
      }, {
        // Add timeout and additional headers for better error handling
        timeout: 10000,
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        }
      });

      // Remove request interceptor after use
      axios.interceptors.request.eject(requestInterceptor);

      // Store token and user data
      const { token, user: loggedInUser } = response.data;
      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(loggedInUser));

      // Set default authorization header for all requests
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      // Initialize socket connection
      initializeSocket(token);

      setUser(loggedInUser);
      return loggedInUser;
    } catch (error) {
      // Enhanced error logging for better debugging
      console.error('Login error:', error);

      if (error.response) {
        // The server responded with a status code outside the 2xx range
        console.error('Response error data:', error.response.data);
        console.error('Response error status:', error.response.status);
      } else if (error.request) {
        // The request was made but no response was received
        console.error('No response received:', error.request);
      }

      throw error;
    }
  };

  const logout = () => {
    // Remove token and user data from storage
    localStorage.removeItem('token');
    localStorage.removeItem('user');

    // Remove authorization header
    delete axios.defaults.headers.common['Authorization'];

    // Close socket connection
    closeSocket();

    setUser(null);
  };

  const checkEmailExists = async (email) => {
    try {
      const response = await axios.get(`${API_URL}/api/auth/check-email?email=${email}`);
      return response.data.exists;
    } catch (error) {
      console.error('Email check error:', error.response || error);
      throw error;
    }
  };

  const getAuthToken = () => {
    return localStorage.getItem('token');
  };

  return (
    <AuthContext.Provider
      value={{
        user,
        loading,
        register,
        login,
        logout,
        checkEmailExists,
        getAuthToken
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
