import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  Box,
  Button,
  Container,
  FormControl,
  FormHelperText,
  Grid,
  InputLabel,
  MenuItem,
  Paper,
  Select,
  TextField,
  Typography,
  Alert,
  Snackbar,
  CircularProgress,
  Checkbox,
  FormControlLabel,
  Stack
} from '@mui/material';
import { useAuth } from '../../contexts/AuthContext';
import { useTickets } from '../../contexts/TicketContext';
import { TICKET_ENDPOINTS, API_URL } from '../../utils/apiConfig';

const CreateTicket = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const { addTicket } = useTickets();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [createdTicketId, setCreatedTicketId] = useState(null);
  const [createdTicketNumber, setCreatedTicketNumber] = useState(null);
  const [connectionStatus, setConnectionStatus] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);

  const [formData, setFormData] = useState({
    category: '',
    productType: '',
    productName: '',
    serialNumber: '',
    warrantyStatus: false,
    warrantyExpiry: '',
    priority: 'medium',
    description: '',
    numberOfUsers: 1
  });

  const [validation, setValidation] = useState({
    category: {
      error: false,
      message: ''
    },
    description: {
      error: false,
      message: ''
    },
    priority: {
      error: false,
      message: ''
    }
  });

  // Category options
  const categories = [
    'Hardware',
    'Software',
    'Network',
    'Account',
    'Other'
  ];

  // Product type options based on category
  const productTypes = {
    Hardware: ['Laptop', 'Desktop', 'Printer', 'Server', 'Mobile Device', 'Other'],
    Software: ['Kaspersky', 'Sophos', 'Fortinet', 'Microsoft', 'Barracuda', 'Operating System', 'Application', 'Database', 'Security', 'Other'],
    Network: ['Internet', 'Intranet', 'VPN', 'Email', 'Other'],
    Account: ['Password Reset', 'Access Request', 'Permission Issue', 'Other'],
    Other: ['General Inquiry', 'Training Request', 'Feedback', 'Other']
  };

  // Priority options
  const priorities = [
    { value: 'low', label: 'Low' },
    { value: 'medium', label: 'Medium' },
    { value: 'high', label: 'High' },
    { value: 'urgent', label: 'Urgent' },
    { value: 'critical', label: 'Critical' }
  ];

  // Validate form fields
  const validateForm = () => {
    let isValid = true;
    const newValidation = { ...validation };

    // Validate category
    if (!formData.category) {
      newValidation.category = {
        error: true,
        message: 'Category is required'
      };
      isValid = false;
    } else {
      newValidation.category = {
        error: false,
        message: ''
      };
    }

    // Description is now optional, only validate length if provided
    if (formData.description && formData.description.length > 1000) {
      newValidation.description = {
        error: true,
        message: 'Description cannot exceed 1000 characters'
      };
      isValid = false;
    } else {
      newValidation.description = {
        error: false,
        message: ''
      };
    }

    // Validate priority
    if (!formData.priority) {
      newValidation.priority = {
        error: true,
        message: 'Priority is required'
      };
      isValid = false;
    } else {
      newValidation.priority = {
        error: false,
        message: ''
      };
    }

    setValidation(newValidation);
    return isValid;
  };

  // Handle input changes
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    // Handle checkbox inputs
    if (type === 'checkbox') {
      setFormData({
        ...formData,
        [name]: checked
      });
      return;
    }

    // Reset product type when category changes
    if (name === 'category') {
      setFormData({
        ...formData,
        [name]: value,
        productType: ''
      });

      // Clear category validation error when value is selected
      if (value) {
        setValidation({
          ...validation,
          category: {
            error: false,
            message: ''
          }
        });
      }
      return;
    }

    // Validate description length
    if (name === 'description') {
      if (value.length > 1000) {
        setValidation({
          ...validation,
          description: {
            error: true,
            message: 'Description cannot exceed 1000 characters'
          }
        });
      } else {
        setValidation({
          ...validation,
          description: {
            error: false,
            message: ''
          }
        });
      }
    }

    // Clear priority validation error when value is selected
    if (name === 'priority' && value) {
      setValidation({
        ...validation,
        priority: {
          error: false,
          message: ''
        }
      });
    }

    setFormData({
      ...formData,
      [name]: value
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate form before submission
    if (!validateForm()) {
      setError('Please fix the errors before submitting');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      // Log detailed information for debugging
      console.log('Form data being submitted:', formData);
      console.log('API endpoint:', TICKET_ENDPOINTS.CREATE);
      console.log('Using API URL:', API_URL);
      console.log('User info:', user);

      // Properly structure the data for backend processing
      const submissionData = {
        category: formData.category,
        priority: formData.priority,
        description: formData.description,
        numberOfUsers: formData.numberOfUsers || 1,
        // Structure product data properly according to Ticket model
        product: {
          name: formData.productName || formData.category || 'Unknown Product',
          type: formData.productType || '',
          serialNumber: formData.serialNumber || '',
          warrantyStatus: {
            inWarranty: formData.warrantyStatus || false,
            expiryDate: formData.warrantyStatus && formData.warrantyExpiry ? formData.warrantyExpiry : null
          }
        },
        // Add client information directly to the ticket for easier access
        clientInfo: {
          firstName: user.firstName,
          lastName: user.lastName,
          companyName: user.companyName,
          username: user.username,
          department: user.department,
          phoneNumber: user.phoneNumber
        }
      };

      console.log('Submission data after preprocessing:', submissionData);
      console.log('Auth token present:', !!localStorage.getItem('token'));

      const response = await axios.post(
        TICKET_ENDPOINTS.CREATE,
        submissionData,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
            'Content-Type': 'application/json'
          },
          // Add timeout to prevent hanging requests
          timeout: 10000
        }
      );

      console.log('Ticket creation response:', response.data);

      if (response.data.ticket) {
        setSuccess(true);
        setCreatedTicketId(response.data.ticket._id);
        setCreatedTicketNumber(response.data.ticket.ticketNumber);

        // Show success message briefly before redirecting
        setSuccessMessage(`Ticket ${response.data.ticket.ticketNumber} created successfully!`);

        // Add the new ticket to our local tickets state
        console.log('Adding ticket to dashboard state:', response.data.ticket);
        addTicket(response.data.ticket);

        // Determine correct dashboard path based on user role
        const dashboardPath = user.role === 'client' 
          ? '/client/dashboard' 
          : user.role === 'technician' 
            ? '/technician/dashboard' 
            : '/admin/dashboard';

        // Clear form data on success
        setFormData({
          category: '',
          productType: '',
          productName: '',
          serialNumber: '',
          warrantyStatus: false,
          warrantyExpiry: '',
          priority: 'medium',
          description: '',
          numberOfUsers: 1
        });

        // Redirect to dashboard after a brief delay with refresh state
        setTimeout(() => {
          navigate(dashboardPath, { state: { refresh: true } });
        }, 1500);
      } else {
        // Handle unexpected success response format
        setError('Unexpected response format from server');
        console.error('Unexpected response format:', response.data);
      }
    } catch (error) {
      console.error('Error creating ticket:', error);
      setSuccess(false);

      if (error.response) {
        // The request was made and the server responded with a status code
        console.error('Response data:', error.response.data);
        console.error('Response status:', error.response.status);
        console.error('Response headers:', error.response.headers);

        setError(error.response.data.message || 'Error creating ticket');
        setConnectionStatus('Connected, but received an error response');
      } else if (error.request) {
        // The request was made but no response was received
        console.error('No response received - Request:', error.request);
        setError('No response received from server. Please try again later.');
        setConnectionStatus('Connection failed - no response');
      } else {
        // Something happened in setting up the request that triggered an Error
        console.error('Request setup error:', error.message);
        setError('Error creating ticket: ' + error.message);
        setConnectionStatus('Request setup failed');
      }
    } finally {
      setLoading(false);
    }
  };

  // Check API connectivity when component mounts
  useEffect(() => {
    const checkConnection = async () => {
      try {
        setConnectionStatus('checking');

        // Log connection details
        console.log('Checking API connection to:', API_URL);
        console.log('Auth token:', localStorage.getItem('token') ? 'Present' : 'Missing');

        // Attempt a simple request to verify connection
        const response = await axios.get(
          `${API_URL}/auth/verify`,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem('token')}`
            },
            timeout: 5000
          }
        );

        console.log('Connection check response:', response.data);
        setConnectionStatus('connected');
      } catch (err) {
        console.error('API connection check failed:', err);

        if (err.response) {
          console.log('Response status:', err.response.status);
          console.log('Response data:', err.response.data);

          // Auth error but connection works
          if (err.response.status === 401) {
            setConnectionStatus('auth_error');
          } else {
            setConnectionStatus('error');
          }
        } else if (err.request) {
          // No response received - likely connection issue
          console.log('No response received from server');
          setConnectionStatus('no_connection');
        } else {
          setConnectionStatus('unknown_error');
        }
      }
    };

    checkConnection();
  }, []);

  // Handle snackbar close
  const handleCloseSnackbar = () => {
    setError(null);
    setSuccess(false);
  };

  // Handle view ticket button click
  const handleViewTicket = () => {
    if (createdTicketId) {
      navigate(`/tickets/${createdTicketId}`);
    }
  };

  return (
    <Container maxWidth="md">
      {connectionStatus && connectionStatus !== 'connected' && (
        <Alert severity="warning" sx={{ mt: 2 }}>
          {connectionStatus === 'checking' && 'Checking connection to API server...'}
          {connectionStatus === 'no_connection' && 'Cannot connect to the API server. Please check if the server is running on port 5002.'}
          {connectionStatus === 'auth_error' && 'Connected to API server but authentication failed. Please log in again.'}
          {connectionStatus === 'error' && 'Error connecting to API server. Check console for details.'}
          {connectionStatus === 'unknown_error' && 'Unknown error occurred during API connection check.'}
        </Alert>
      )}


<Grid container spacing={3} sx={{ mb: 4 }}>
          <Grid item xs={12} sm={4}>
            <StatsCard 
              title="Total Tickets" 
              value={stats.totalTickets} 
              color="#e3f2fd" 
            />
          </Grid>
          <Grid item xs={12} sm={4}>
            <StatsCard 
              title="Active Tickets" 
              value={stats.activeTickets} 
              color="#fff8e1" 
            />
          </Grid>
          <Grid item xs={12} sm={4}>
            <StatsCard 
              title="Resolved Tickets" 
              value={stats.resolvedTickets} 
              color="#e8f5e9" 
            />
          </Grid>
        </Grid>

      <Paper elevation={3} sx={{ p: 4, mt: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Create Support Ticket
        </Typography>

        <Box component="form" onSubmit={handleSubmit} noValidate>
          <Grid container spacing={3}>
            {/* Category Selection */}
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth required error={validation.category.error}>
                <InputLabel id="category-label">Category</InputLabel>
                <Select
                  labelId="category-label"
                  id="category"
                  name="category"
                  value={formData.category}
                  onChange={handleChange}
                  label="Category"
                >
                  {categories.map((category) => (
                    <MenuItem key={category} value={category}>
                      {category}
                    </MenuItem>
                  ))}
                </Select>
                {validation.category.error && (
                  <FormHelperText error>{validation.category.message}</FormHelperText>
                )}
              </FormControl>
            </Grid>

            {/* Product Type Selection (conditional) */}
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth disabled={!formData.category}>
                <InputLabel id="productType-label">Product Type</InputLabel>
                <Select
                  labelId="productType-label"
                  id="productType"
                  name="productType"
                  value={formData.productType}
                  onChange={handleChange}
                  label="Product Type"
                >
                  {formData.category && productTypes[formData.category]?.map((type) => (
                    <MenuItem key={type} value={type}>
                      {type}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>

            {/* Product Name */}
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                id="productName"
                name="productName"
                label="Product Name"
                value={formData.productName}
                onChange={handleChange}
                helperText="Optional: Specific product name if applicable"
              />
            </Grid>

            {/* Serial Number */}
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                id="serialNumber"
                name="serialNumber"
                label="Serial Number"
                value={formData.serialNumber}
                onChange={handleChange}
                helperText="Optional: Product serial number if available"
              />
            </Grid>

            {/* Warranty Status */}
            <Grid item xs={12} sm={6}>
              <FormControlLabel
                control={
                  <Checkbox
                    checked={formData.warrantyStatus}
                    onChange={handleChange}
                    name="warrantyStatus"
                  />
                }
                label="Product is under warranty"
              />
            </Grid>

            {/* Warranty Expiry Date (conditional) */}
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                id="warrantyExpiry"
                name="warrantyExpiry"
                label="Warranty Expiry Date"
                type="date"
                value={formData.warrantyExpiry}
                onChange={handleChange}
                disabled={!formData.warrantyStatus}
                InputLabelProps={{
                  shrink: true,
                }}
                helperText="Only if product is under warranty"
              />
            </Grid>

            {/* Priority Selection */}
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth required error={validation.priority.error}>
                <InputLabel id="priority-label">Priority</InputLabel>
                <Select
                  labelId="priority-label"
                  id="priority"
                  name="priority"
                  value={formData.priority}
                  onChange={handleChange}
                  label="Priority"
                >
                  {priorities.map((priority) => (
                    <MenuItem key={priority.value} value={priority.value}>
                      {priority.label}
                    </MenuItem>
                  ))}
                </Select>
                {validation.priority.error && (
                  <FormHelperText error>{validation.priority.message}</FormHelperText>
                )}
              </FormControl>
            </Grid>

            {/* Number of Users Affected */}
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                id="numberOfUsers"
                name="numberOfUsers"
                label="Number of Users Affected"
                type="number"
                value={formData.numberOfUsers}
                onChange={handleChange}
                InputProps={{ inputProps: { min: 1 } }}
                helperText="How many users are affected by this issue?"
              />
            </Grid>

            {/* Description */}
            <Grid item xs={12}>
              <TextField
                fullWidth
                id="description"
                name="description"
                label="Description"
                multiline
                rows={4}
                value={formData.description}
                onChange={handleChange}
                error={validation.description.error}
                helperText={validation.description.error ? validation.description.message : "Describe the issue in detail (max 1000 characters)"}
              />
            </Grid>

            {/* Submit Button */}
            <Grid item xs={12}>
              <Box sx={{ position: 'relative' }}>
                <Button
                  type="submit"
                  variant="contained"
                  color="primary"
                  disabled={loading}
                  sx={{ mt: 2 }}
                >
                  Create Ticket
                </Button>
                {loading && (
                  <CircularProgress
                    size={24}
                    sx={{
                      position: 'absolute',
                      top: '50%',
                      left: '50%',
                      marginTop: '-12px',
                      marginLeft: '-12px',
                    }}
                  />
                )}
              </Box>
            </Grid>
          </Grid>
        </Box>
      </Paper>

      {/* Success or Error Messages */}
      <Snackbar
        open={success || error !== null}
        autoHideDuration={6000}
        onClose={handleCloseSnackbar}
      >
        <Alert
          onClose={handleCloseSnackbar}
          severity={success ? "success" : "error"}
          sx={{ width: '100%' }}
        >
          {success ? successMessage : error}
        </Alert>
      </Snackbar>

      {/* View Ticket Button (appears after successful creation) */}
      {success && (
        <Box sx={{ mt: 2, textAlign: 'center' }}>
          <Button
            variant="outlined"
            color="primary"
            onClick={handleViewTicket}
          >
            View Ticket #{createdTicketNumber}
          </Button>
        </Box>
      )}
    </Container>
  );
};

export default CreateTicket;
