import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  Box,
  Button,
  Container,
  FormControl,
  FormHelperText,
  Grid,
  InputLabel,
  MenuItem,
  Paper,
  Select,
  TextField,
  Typography,
  Alert,
  Snackbar,
  CircularProgress
} from '@mui/material';
import { useAuth } from '../../contexts/AuthContext';
import { TICKET_ENDPOINTS } from '../../utils/apiConfig';

const CreateTicket = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  const [formData, setFormData] = useState({
    category: '',
    productType: '',
    productName: '',
    serialNumber: '',
    warrantyStatus: false,
    warrantyExpiry: '',
    priority: 'medium',
    description: '',
    numberOfUsers: 1
  });

  const [validation, setValidation] = useState({
    description: {
      error: false,
      message: ''
    }
  });

  // Category options
  const categories = [
    'Hardware',
    'Software',
    'Network',
    'Account',
    'Other'
  ];

  // Product type options based on category
  const productTypes = {
    Hardware: ['Laptop', 'Desktop', 'Printer', 'Server', 'Mobile Device', 'Other'],
    Software: ['Operating System', 'Application', 'Database', 'Security', 'Other'],
    Network: ['Internet', 'Intranet', 'VPN', 'Email', 'Other'],
    Account: ['Password Reset', 'Access Request', 'Permission Issue', 'Other'],
    Other: ['General Inquiry', 'Training Request', 'Feedback', 'Other']
  };

  // Priority options
  const priorities = [
    { value: 'low', label: 'Low' },
    { value: 'medium', label: 'Medium' },
    { value: 'high', label: 'High' },
    { value: 'critical', label: 'Critical' }
  ];

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;

    // Reset product type when category changes
    if (name === 'category') {
      setFormData({
        ...formData,
        [name]: value,
        productType: ''
      });
      return;
    }

    // Validate description length
    if (name === 'description') {
      if (value.length > 1000) {
        setValidation({
          ...validation,
          description: {
            error: true,
            message: 'Description cannot exceed 1000 characters'
          }
        });
      } else {
        setValidation({
          ...validation,
          description: {
            error: false,
            message: ''
          }
        });
      }
    }

    setFormData({
      ...formData,
      [name]: value
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate form
    if (!formData.category) {
      setError('Please select a category');
      return;
    }

    if (!formData.description) {
      setError('Please provide a description');
      return;
    }

    if (validation.description.error) {
      setError('Please fix the errors before submitting');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await axios.post(
        TICKET_ENDPOINTS.CREATE,
        formData,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        }
      );

      setSuccess(true);

      // Redirect to ticket details after a short delay
      setTimeout(() => {
        navigate(`/tickets/${response.data.ticket._id}`);
      }, 1500);

    } catch (err) {
      console.error('Error creating ticket:', err);
      setError(err.response?.data?.message || 'An error occurred while creating the ticket');
    } finally {
      setLoading(false);
    }
  };

  // Handle snackbar close
  const handleCloseSnackbar = () => {
    setError(null);
    setSuccess(false);
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} sx={{ p: 4, mt: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Create Support Ticket
        </Typography>

        <Box component="form" onSubmit={handleSubmit} noValidate>
          <Grid container spacing={3}>
            {/* Category Selection */}
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth required>
                <InputLabel id="category-label">Category</InputLabel>
                <Select
                  labelId="category-label"
                  id="category"
                  name="category"
                  value={formData.category}
                  onChange={handleChange}
                  label="Category"
                >
                  {categories.map((category) => (
                    <MenuItem key={category} value={category}>
                      {category}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>

            {/* Product Type Selection (conditional based on category) */}
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth disabled={!formData.category}>
                <InputLabel id="product-type-label">Product Type</InputLabel>
                <Select
                  labelId="product-type-label"
                  id="productType"
                  name="productType"
                  value={formData.productType}
                  onChange={handleChange}
                  label="Product Type"
                >
                  {formData.category && productTypes[formData.category]?.map((type) => (
                    <MenuItem key={type} value={type}>
                      {type}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>

            {/* Product Name */}
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                id="productName"
                name="productName"
                label="Product Name"
                value={formData.productName}
                onChange={handleChange}
                helperText="E.g., Dell XPS 15, Microsoft Office, etc."
              />
            </Grid>

            {/* Serial Number (for hardware) */}
            {formData.category === 'Hardware' && (
              <Grid item xs={12} sm={6}>
                <TextField
                  fullWidth
                  id="serialNumber"
                  name="serialNumber"
                  label="Serial Number"
                  value={formData.serialNumber}
                  onChange={handleChange}
                  helperText="Optional: Enter device serial number"
                />
              </Grid>
            )}

            {/* Warranty Status (for hardware) */}
            {formData.category === 'Hardware' && (
              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <InputLabel id="warranty-status-label">Warranty Status</InputLabel>
                  <Select
                    labelId="warranty-status-label"
                    id="warrantyStatus"
                    name="warrantyStatus"
                    value={formData.warrantyStatus}
                    onChange={handleChange}
                    label="Warranty Status"
                  >
                    <MenuItem value={true}>In Warranty</MenuItem>
                    <MenuItem value={false}>Out of Warranty</MenuItem>
                  </Select>
                </FormControl>
              </Grid>
            )}

            {/* Warranty Expiry Date (if in warranty) */}
            {formData.category === 'Hardware' && formData.warrantyStatus && (
              <Grid item xs={12} sm={6}>
                <TextField
                  fullWidth
                  id="warrantyExpiry"
                  name="warrantyExpiry"
                  label="Warranty Expiry Date"
                  type="date"
                  value={formData.warrantyExpiry}
                  onChange={handleChange}
                  InputLabelProps={{ shrink: true }}
                />
              </Grid>
            )}

            {/* Number of Users Affected (for software/network) */}
            {(formData.category === 'Software' || formData.category === 'Network') && (
              <Grid item xs={12} sm={6}>
                <TextField
                  fullWidth
                  id="numberOfUsers"
                  name="numberOfUsers"
                  label="Number of Users Affected"
                  type="number"
                  value={formData.numberOfUsers}
                  onChange={handleChange}
                  InputProps={{ inputProps: { min: 1 } }}
                />
              </Grid>
            )}

            {/* Priority Selection */}
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth required>
                <InputLabel id="priority-label">Priority</InputLabel>
                <Select
                  labelId="priority-label"
                  id="priority"
                  name="priority"
                  value={formData.priority}
                  onChange={handleChange}
                  label="Priority"
                >
                  {priorities.map((priority) => (
                    <MenuItem key={priority.value} value={priority.value}>
                      {priority.label}
                    </MenuItem>
                  ))}
                </Select>
                <FormHelperText>
                  Select the appropriate priority level for your issue
                </FormHelperText>
              </FormControl>
            </Grid>

            {/* Description */}
            <Grid item xs={12}>
              <TextField
                fullWidth
                id="description"
                name="description"
                label="Description"
                multiline
                rows={4}
                value={formData.description}
                onChange={handleChange}
                required
                error={validation.description.error}
                helperText={validation.description.error ? validation.description.message : 'Please provide a detailed description of the issue'}
              />
              <Typography variant="caption" color="textSecondary" sx={{ mt: 1, display: 'block' }}>
                Characters: {formData.description.length}/1000
              </Typography>
            </Grid>

            {/* Submit Button */}
            <Grid item xs={12}>
              <Button
                type="submit"
                variant="contained"
                color="primary"
                size="large"
                disabled={loading || validation.description.error}
                sx={{ mt: 2 }}
              >
                {loading ? <CircularProgress size={24} /> : 'Submit Ticket'}
              </Button>
            </Grid>
          </Grid>
        </Box>
      </Paper>

      {/* Error Snackbar */}
      <Snackbar open={!!error} autoHideDuration={6000} onClose={handleCloseSnackbar}>
        <Alert onClose={handleCloseSnackbar} severity="error" sx={{ width: '100%' }}>
          {error}
        </Alert>
      </Snackbar>

      {/* Success Snackbar */}
      <Snackbar open={success} autoHideDuration={6000} onClose={handleCloseSnackbar}>
        <Alert onClose={handleCloseSnackbar} severity="success" sx={{ width: '100%' }}>
          Ticket created successfully! Redirecting...
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default CreateTicket;
