import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Grid,
  Paper,
  Typography,
  Box,
  Card,
  CardContent,
  Button,
  Tab,
  Tabs,
  CircularProgress,
  Fab,
  Snackbar,
  Alert
} from '@mui/material';
import axios from 'axios';
import AddIcon from '@mui/icons-material/Add';
import AssignmentIndIcon from '@mui/icons-material/AssignmentInd';
import { green, blue, red, orange, grey } from '@mui/material/colors';
import { useAuth } from '../../contexts/AuthContext';
import { useTickets } from '../../contexts/TicketContext';
import TicketList from '../../components/tickets/TicketList';
import { API_URL } from '../../utils/apiConfig';

const ClientDashboard = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const { tickets, loading: ticketsLoading, fetchTickets, assignAllUnassignedTickets } = useTickets();
  const [stats, setStats] = useState({
    totalTickets: 0,
    activeTickets: 0,
    resolvedTickets: 0
  });
  const [selectedTab, setSelectedTab] = useState(0);
  const [loading, setLoading] = useState(true);
  const [assigning, setAssigning] = useState(false);
  const [notification, setNotification] = useState({ open: false, message: '', severity: 'info' });

  useEffect(() => {
    // Fetch tickets using the context
    fetchTickets().then(() => {
      fetchStats();
      setLoading(false);
    }).catch(error => {
      console.error('Error fetching tickets:', error);
      setLoading(false);
    });
  }, [fetchTickets]);

  // Separate function to fetch just the stats
  const fetchStats = async () => {
    try {
      console.log('Fetching stats from API URL:', API_URL);
      const statsRes = await axios.get(`${API_URL}/tickets/stats`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });

      console.log('Stats response:', statsRes.data);
      setStats(statsRes.data);
    } catch (error) {
      console.error('Error fetching stats:', error);
      console.error('Error details:', error.response?.data || error.message);
    }
  };

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
  };

  const handleCreateTicket = () => {
    navigate('/client/tickets/create');
  };

  const handleAssignAllToCloud4Rain = async () => {
    try {
      setAssigning(true);
      const result = await assignAllUnassignedTickets('cloud4rain');

      setNotification({
        open: true, 
        message: result.message,
        severity: 'success'
      });

      // Refresh stats after assignment
      fetchStats();
    } catch (error) {
      console.error('Error assigning tickets:', error);
      setNotification({
        open: true,
        message: error.message || 'Failed to assign tickets to cloud4rain',
        severity: 'error'
      });
    } finally {
      setAssigning(false);
    }
  };

  const handleCloseNotification = () => {
    setNotification(prev => ({ ...prev, open: false }));
  };

  const getFilteredTickets = () => {
    if (!tickets || !Array.isArray(tickets)) {
      console.warn('Tickets is not an array:', tickets);
      return [];
    }

    switch (selectedTab) {
      case 0: // All tickets
        return tickets;
      case 1: // Active tickets
        return tickets.filter(ticket => ['new', 'in_progress', 'on_hold'].includes(ticket.status));
      case 2: // Resolved tickets
        return tickets.filter(ticket => ['resolved', 'closed'].includes(ticket.status));
      default:
        return tickets;
    }
  };

  if (loading || ticketsLoading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '80vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box sx={{ p: 3 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4">Welcome, {user.name}</Typography>
        <Box>
          <Button
            variant="contained"
            startIcon={<AssignmentIndIcon />}
            onClick={handleAssignAllToCloud4Rain}
            sx={{ mr: 2 }}
            disabled={assigning}
          >
            {assigning ? 'Assigning...' : 'Assign All to Cloud4Rain'}
          </Button>
          <Button
            variant="contained"
            startIcon={<AddIcon />}
            onClick={handleCreateTicket}
          >
            Create New Ticket
          </Button>
        </Box>
      </Box>

      <Grid container spacing={3}>
        {/* Stats Cards */}
        <Grid item xs={12} sm={6} md={4}>
          <Card>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <Typography color="textSecondary" variant="h6">
                  Total Tickets
                </Typography>
              </Box>
              <Typography variant="h4">{stats.totalTickets}</Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <Card>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <Typography color="textSecondary" variant="h6">
                  Active Tickets
                </Typography>
              </Box>
              <Typography variant="h4">{stats.activeTickets}</Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <Card>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <Typography color="textSecondary" variant="h6">
                  Resolved Tickets
                </Typography>
              </Box>
              <Typography variant="h4">{stats.resolvedTickets}</Typography>
            </CardContent>
          </Card>
        </Grid>

        {/* Company Information */}
        <Grid item xs={12}>
          <Paper sx={{ p: 2, mb: 3 }}>
            <Typography variant="h6" sx={{ mb: 2 }}>Company Information</Typography>
            <Grid container spacing={2}>
              <Grid item xs={12} md={4}>
                <Typography variant="subtitle2" color="textSecondary">Company Name</Typography>
                <Typography variant="body1">{user.company}</Typography>
              </Grid>
              <Grid item xs={12} md={4}>
                <Typography variant="subtitle2" color="textSecondary">Department</Typography>
                <Typography variant="body1">{user.department}</Typography>
              </Grid>
              <Grid item xs={12} md={4}>
                <Typography variant="subtitle2" color="textSecondary">Contact</Typography>
                <Typography variant="body1">{user.phone}</Typography>
              </Grid>
            </Grid>
          </Paper>
        </Grid>

        {/* Tickets Section */}
        <Grid item xs={12}>
          <Paper sx={{ p: 2 }}>
            <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 2 }}>
              <Tabs value={selectedTab} onChange={handleTabChange}>
                <Tab label="All Tickets" />
                <Tab label="Active" />
                <Tab label="Resolved" />
              </Tabs>
            </Box>
            <TicketList tickets={getFilteredTickets()} />
          </Paper>
        </Grid>
      </Grid>

      {/* Floating action button for mobile */}
      <Box sx={{ display: { xs: 'block', md: 'none' } }}>
        <Fab
          color="primary"
          sx={{ position: 'fixed', bottom: 16, right: 16 }}
          onClick={handleCreateTicket}
        >
          <AddIcon />
        </Fab>
      </Box>

      {/* Notification */}
      <Snackbar 
        open={notification.open} 
        autoHideDuration={6000} 
        onClose={handleCloseNotification}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert 
          onClose={handleCloseNotification} 
          severity={notification.severity} 
          sx={{ width: '100%' }}
        >
          {notification.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default ClientDashboard;
