/**
 * API Configuration
 * 
 * This file centralizes API URL configuration for the application.
 * It ensures consistent API endpoints across components.
 */

// Use environment variable or default to localhost:5002
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5002';

export const API_URL = `${API_BASE_URL}/api`;

// Socket.io connection URL (same as API base URL)
export const SOCKET_URL = API_BASE_URL;

// Auth endpoints
export const AUTH_ENDPOINTS = {
  LOGIN: `${API_URL}/auth/login`,
  REGISTER: `${API_URL}/auth/register`,
  VERIFY: `${API_URL}/auth/verify`,
  PROFILE: `${API_URL}/auth/profile`
};

// Ticket endpoints
export const TICKET_ENDPOINTS = {
  CREATE: `${API_URL}/tickets`,
  GET_ALL: `${API_URL}/tickets`,
  GET_BY_ID: (id) => `${API_URL}/tickets/${id}`,
  UPDATE_STATUS: (id) => `${API_URL}/tickets/${id}/status`,
  ASSIGN: (id) => `${API_URL}/tickets/${id}/assign`,
  ADD_MESSAGE: (id) => `${API_URL}/tickets/${id}/messages`,
  GET_MESSAGES: (id) => `${API_URL}/tickets/${id}/messages`,
  GET_STATS: `${API_URL}/tickets/stats`
};

// User endpoints
export const USER_ENDPOINTS = {
  GET_ALL: `${API_URL}/users`,
  GET_BY_ID: (id) => `${API_URL}/users/${id}`,
  UPDATE: (id) => `${API_URL}/users/${id}`,
  GET_TECHNICIANS: `${API_URL}/users/technicians`
};

// Admin endpoints
export const ADMIN_ENDPOINTS = {
  DASHBOARD: `${API_URL}/admin/dashboard`,
  USERS: `${API_URL}/admin/users`,
  TICKETS: `${API_URL}/admin/tickets`
};

export default {
  API_URL,
  SOCKET_URL,
  AUTH_ENDPOINTS,
  TICKET_ENDPOINTS,
  USER_ENDPOINTS,
  ADMIN_ENDPOINTS
};
