import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  Box,
  Grid,
  Chip,
  Divider,
  TextField,
  Button,
  CircularProgress,
  Card,
  CardContent,
  Avatar,
  IconButton,
  Menu,
  MenuItem,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Alert,
  Badge
} from '@mui/material';
import {
  Send as SendIcon,
  MoreVert as MoreVertIcon,
  AttachFile as AttachFileIcon,
  Check as CheckIcon
} from '@mui/icons-material';
import axios from 'axios';
import { useAuth } from '../../contexts/AuthContext';
import { 
  getSocket, 
  joinTicketRoom, 
  onSocketEvent, 
  offSocketEvent, 
  sendTicketMessage,
  acceptTicket 
} from '../../services/socketService';

const getStatusColor = (status) => {
  switch (status) {
    case 'new':
      return 'info';
    case 'in_progress':
      return 'warning';
    case 'on_hold':
      return 'error';
    case 'resolved':
      return 'success';
    default:
      return 'default';
  }
};

const getPriorityColor = (priority) => {
  switch (priority) {
    case 'urgent':
      return 'error';
    case 'high':
      return 'warning';
    case 'medium':
      return 'info';
    case 'low':
      return 'success';
    default:
      return 'default';
  }
};

const TicketDetails = () => {
  const { id } = useParams();
  const { user } = useAuth();
  const [ticket, setTicket] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [newMessageAlert, setNewMessageAlert] = useState(false);
  const messagesEndRef = useRef(null);
  const messagesContainerRef = useRef(null);
  const [anchorEl, setAnchorEl] = useState(null);
  const [statusDialog, setStatusDialog] = useState(false);
  const [newStatus, setNewStatus] = useState('');
  const [acceptingTicket, setAcceptingTicket] = useState(false);

  useEffect(() => {
    joinTicketRoom(id);

    const handleMessageReceived = (data) => {
      setMessages(prev => [...prev, data.message]);

      // Check if the message is from someone else
      if (data.message.sender._id !== user._id) {
        setNewMessageAlert(true);
      }

      // If the user is at the bottom of the chat, scroll to the new message
      const container = messagesContainerRef.current;
      if (container && container.scrollHeight - container.scrollTop <= container.clientHeight + 100) {
        scrollToBottom();
      }
    };

    const handleTicketAccepted = (data) => {
      if (data.ticketId === id) {
        setTicket(prev => ({
          ...prev,
          assignedTo: {
            _id: data.technicianId,
            name: data.technicianName
          },
          status: 'in_progress'
        }));
      }
    };

    onSocketEvent('message_received', handleMessageReceived);
    onSocketEvent('ticket_accepted', handleTicketAccepted);

    fetchTicketDetails();

    return () => {
      offSocketEvent('message_received', handleMessageReceived);
      offSocketEvent('ticket_accepted', handleTicketAccepted);
    };
  }, [id, user]);

  const fetchTicketDetails = async () => {
    try {
      const [ticketRes, messagesRes] = await Promise.all([
        axios.get(`${process.env.REACT_APP_API_URL}/api/tickets/${id}`),
        axios.get(`${process.env.REACT_APP_API_URL}/api/tickets/${id}/messages`)
      ]);
      setTicket(ticketRes.data);
      setMessages(messagesRes.data);
      scrollToBottom();
    } catch (err) {
      console.error('Error fetching ticket details:', err);
      setError(err.response?.data?.message || 'Failed to load ticket details');
    } finally {
      setLoading(false);
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    setNewMessageAlert(false);
  };

  const handleSendMessage = async () => {
    if (!message.trim()) return;

    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/tickets/${id}/messages`, {
        content: message,
        sender: user._id
      });

      // Determine the recipient (if client, send to assigned technician, if technician, send to client)
      const recipientId = user.role === 'client' 
        ? ticket.assignedTo?._id 
        : ticket.client._id;

      sendTicketMessage({
        ticketId: id,
        message: response.data,
        recipientId
      });

      setMessage('');
      scrollToBottom();
    } catch (err) {
      console.error('Error sending message:', err);
      setError(err.response?.data?.message || 'Failed to send message');
    }
  };

  const handleStatusChange = async () => {
    try {
      await axios.patch(`${process.env.REACT_APP_API_URL}/api/tickets/${id}/status`, {
        status: newStatus
      });
      setTicket(prev => ({ ...prev, status: newStatus }));
      setStatusDialog(false);
      setAnchorEl(null);
    } catch (err) {
      console.error('Error updating ticket status:', err);
      setError(err.response?.data?.message || 'Failed to update ticket status');
    }
  };

  const handleAcceptTicket = async () => {
    if (!user || user.role !== 'technician') return;

    setAcceptingTicket(true);

    try {
      // Update ticket in the database
      const response = await axios.patch(`${process.env.REACT_APP_API_URL}/api/tickets/${id}/assign`, {
        technicianId: user._id
      });

      // Update local state
      setTicket(response.data);

      // Notify the client and other technicians
      acceptTicket({
        ticketId: id,
        clientId: ticket.client._id,
        technicianId: user._id,
        technicianName: user.name
      });

      // Add a system message about the acceptance
      const systemMessage = {
        content: `Ticket has been accepted by ${user.name}`,
        sender: { _id: 'system', name: 'System' },
        timestamp: new Date().toISOString(),
        isSystemMessage: true
      };

      setMessages(prev => [...prev, systemMessage]);

    } catch (err) {
      console.error('Error accepting ticket:', err);
      setError(err.response?.data?.message || 'Failed to accept ticket');
    } finally {
      setAcceptingTicket(false);
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!ticket) {
    return (
      <Container>
        <Alert severity="error">Ticket not found</Alert>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg">
      <Paper sx={{ p: 3, mb: 3 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
          <Typography variant="h5">
            Ticket #{ticket.ticketNumber}
          </Typography>
          {(user.role === 'admin' || user.role === 'technician') && (
            <>
              <IconButton onClick={(e) => setAnchorEl(e.currentTarget)}>
                <MoreVertIcon />
              </IconButton>
              <Menu
                anchorEl={anchorEl}
                open={Boolean(anchorEl)}
                onClose={() => setAnchorEl(null)}
              >
                <MenuItem onClick={() => {
                  setStatusDialog(true);
                  setAnchorEl(null);
                }}>
                  Update Status
                </MenuItem>
              </Menu>
            </>
          )}
        </Box>

        <Grid container spacing={3}>
          <Grid item xs={12} md={6}>
            <Typography variant="subtitle2" color="textSecondary">Status</Typography>
            <Chip
              label={ticket.status.replace('_', ' ')}
              color={getStatusColor(ticket.status)}
              sx={{ mt: 1 }}
            />
          </Grid>
          <Grid item xs={12} md={6}>
            <Typography variant="subtitle2" color="textSecondary">Priority</Typography>
            <Chip
              label={ticket.priority}
              color={getPriorityColor(ticket.priority)}
              sx={{ mt: 1 }}
            />
          </Grid>
          <Grid item xs={12} md={6}>
            <Typography variant="subtitle2" color="textSecondary">Category</Typography>
            <Typography variant="body1">{ticket.category}</Typography>
          </Grid>
          <Grid item xs={12} md={6}>
            <Typography variant="subtitle2" color="textSecondary">Product</Typography>
            <Typography variant="body1">{ticket.product.name}</Typography>
          </Grid>
          {ticket.product.serialNumber && (
            <Grid item xs={12} md={6}>
              <Typography variant="subtitle2" color="textSecondary">Serial Number</Typography>
              <Typography variant="body1">{ticket.product.serialNumber}</Typography>
            </Grid>
          )}
          <Grid item xs={12} md={6}>
            <Typography variant="subtitle2" color="textSecondary">Assigned To</Typography>
            <Typography variant="body1">
              {ticket.assignedTo ? ticket.assignedTo.name : 'Not assigned'}
              {!ticket.assignedTo && user.role === 'technician' && (
                <Button 
                  variant="outlined" 
                  color="primary" 
                  size="small" 
                  startIcon={<CheckIcon />}
                  onClick={handleAcceptTicket}
                  disabled={acceptingTicket}
                  sx={{ ml: 2 }}
                >
                  {acceptingTicket ? 'Accepting...' : 'Accept Ticket'}
                </Button>
              )}
            </Typography>
          </Grid>
          <Grid item xs={12}>
            <Typography variant="subtitle2" color="textSecondary">Description</Typography>
            <Typography variant="body1">{ticket.description}</Typography>
          </Grid>
        </Grid>
      </Paper>

      {/* Chat Section */}
      <Paper sx={{ p: 3 }}>
        <Typography variant="h6" gutterBottom>
          Communication
        </Typography>
        <Box 
          ref={messagesContainerRef}
          sx={{ 
            height: '400px', 
            overflowY: 'auto', 
            mb: 2,
            position: 'relative' 
          }}
        >
          {messages.map((msg, index) => (
            <Box
              key={index}
              sx={{
                display: 'flex',
                justifyContent: msg.sender._id === user._id ? 'flex-end' : 'flex-start',
                mb: 2
              }}
            >
              <Card
                sx={{
                  maxWidth: '70%',
                  bgcolor: msg.isSystemMessage 
                    ? 'grey.200'
                    : msg.sender._id === user._id 
                      ? 'primary.light' 
                      : 'grey.100'
                }}
              >
                <CardContent sx={{ py: 1, '&:last-child': { pb: 1 } }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                    <Avatar sx={{ width: 24, height: 24, mr: 1 }}>
                      {msg.sender.name[0]}
                    </Avatar>
                    <Typography variant="caption" color="textSecondary">
                      {msg.sender.name} - {new Date(msg.timestamp).toLocaleString()}
                    </Typography>
                  </Box>
                  <Typography variant="body2">{msg.content}</Typography>
                </CardContent>
              </Card>
            </Box>
          ))}
          <div ref={messagesEndRef} />

          {newMessageAlert && (
            <Button 
              variant="contained" 
              color="primary"
              size="small"
              onClick={scrollToBottom}
              sx={{ 
                position: 'absolute', 
                bottom: 10, 
                left: '50%', 
                transform: 'translateX(-50%)',
                zIndex: 1
              }}
            >
              New messages ↓
            </Button>
          )}
        </Box>

        <Divider sx={{ mb: 2 }} />

        <Box sx={{ display: 'flex', gap: 1 }}>
          <TextField
            fullWidth
            placeholder="Type your message..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
          />
          <IconButton color="primary">
            <AttachFileIcon />
          </IconButton>
          <Button
            variant="contained"
            endIcon={<SendIcon />}
            onClick={handleSendMessage}
          >
            Send
          </Button>
        </Box>
      </Paper>

      {/* Status Update Dialog */}
      <Dialog open={statusDialog} onClose={() => setStatusDialog(false)}>
        <DialogTitle>Update Ticket Status</DialogTitle>
        <DialogContent>
          <TextField
            select
            fullWidth
            value={newStatus}
            onChange={(e) => setNewStatus(e.target.value)}
            sx={{ mt: 2 }}
          >
            <MenuItem value="new">New</MenuItem>
            <MenuItem value="in_progress">In Progress</MenuItem>
            <MenuItem value="on_hold">On Hold</MenuItem>
            <MenuItem value="resolved">Resolved</MenuItem>
          </TextField>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setStatusDialog(false)}>Cancel</Button>
          <Button onClick={handleStatusChange} variant="contained">
            Update
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default TicketDetails;
