import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  IconButton,
  Typography,
  Box,
  Avatar,
  Tooltip
} from '@mui/material';
import {
  Visibility as VisibilityIcon,
  Assignment as AssignmentIcon,
  Person as PersonIcon
} from '@mui/icons-material';
import { useAuth } from '../../contexts/AuthContext';

const getStatusColor = (status) => {
  switch (status) {
    case 'new':
      return 'info';
    case 'in_progress':
      return 'warning';
    case 'on_hold':
      return 'error';
    case 'resolved':
      return 'success';
    default:
      return 'default';
  }
};

const getPriorityColor = (priority) => {
  switch (priority) {
    case 'urgent':
      return 'error';
    case 'high':
      return 'warning';
    case 'medium':
      return 'info';
    case 'low':
      return 'success';
    default:
      return 'default';
  }
};

const TicketList = ({ tickets, onAssign, showAssignButton = false }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const { user } = useAuth();

  // Ensure we have a valid user role for navigation
  const userRole = user?.role || 'client';

  const handleViewTicket = (ticketId) => {
    // Navigate to the correct role-specific ticket details page
    navigate(`/${userRole}/tickets/${ticketId}`);
  };

  // Safely handle the case where tickets is undefined or empty
  const validTickets = Array.isArray(tickets) ? tickets : [];

  if (!validTickets.length) {
    return (
      <Box sx={{ p: 3, textAlign: 'center' }}>
        <Typography variant="h6" color="textSecondary">
          No tickets found
        </Typography>
      </Box>
    );
  }

  // Remove any duplicate tickets based on _id
  const uniqueTickets = validTickets.filter((ticket, index, self) => 
    index === self.findIndex(t => t._id === ticket._id)
  );

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Ticket #</TableCell>
            <TableCell>Client</TableCell>
            <TableCell>Product</TableCell>
            <TableCell>Category</TableCell>
            <TableCell>Priority</TableCell>
            <TableCell>Status</TableCell>
            <TableCell>Created</TableCell>
            <TableCell>Assigned To</TableCell>
            <TableCell>Actions</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {uniqueTickets.map((ticket) => {
            // Prepare client display info - use clientDetails if available, otherwise use client
            // For username fallback, split the username at @ for email usernames
            const username = ticket.client?.username || '';
            const usernameDisplay = username.includes('@') ? username.split('@')[0] : username;

            const clientName = 
              (ticket.clientDetails?.firstName && ticket.clientDetails?.lastName) ? 
                `${ticket.clientDetails.firstName} ${ticket.clientDetails.lastName}` :
              (ticket.client?.firstName && ticket.client?.lastName) ? 
                `${ticket.client.firstName} ${ticket.client.lastName}` :
                usernameDisplay || 'Unknown Client';

            const companyName = 
              ticket.clientDetails?.companyName || 
              ticket.client?.companyName || 
              ticket.client?.company || 
              'Unknown Company';

            const department = 
              ticket.clientDetails?.department || 
              ticket.client?.department || '';

            const phoneNumber = 
              ticket.clientDetails?.phoneNumber || 
              ticket.client?.phoneNumber || '';

            return (
            <TableRow key={ticket._id}>
              <TableCell>{ticket.ticketNumber || 'N/A'}</TableCell>
              <TableCell>
                <Typography variant="body2" fontWeight="bold">
                  {companyName}
                </Typography>
                <Typography variant="body2">
                  {clientName}
                  {department && 
                    <span style={{ color: '#666', fontSize: '0.85em' }}>
                      ({department})
                    </span>
                  }
                </Typography>
                {phoneNumber && (
                  <Typography variant="caption" color="textSecondary">
                    {phoneNumber}
                  </Typography>
                )}
              </TableCell>
              <TableCell>
                <Typography variant="body2">{ticket.product?.name || 'Unknown Product'}</Typography>
                <Typography variant="caption" color="textSecondary">
                  {ticket.product?.type || ticket.product?.serialNumber || 'No details'}
                </Typography>
              </TableCell>
              <TableCell>
                <Chip
                  size="small"
                  label={ticket.category}
                  color={ticket.category === 'hardware' ? 'primary' : 'secondary'}
                />
              </TableCell>
              <TableCell>
                <Chip
                  size="small"
                  label={ticket.priority}
                  color={getPriorityColor(ticket.priority)}
                />
              </TableCell>
              <TableCell>
                <Chip
                  size="small"
                  label={ticket.status.replace('_', ' ')}
                  color={getStatusColor(ticket.status)}
                />
              </TableCell>
              <TableCell>
                {new Date(ticket.createdAt).toLocaleDateString()}
              </TableCell>
              <TableCell>
                {ticket.assignedTo ? (
                  <div>
                    {ticket.assignedTo.firstName && ticket.assignedTo.lastName ? 
                      `${ticket.assignedTo.firstName} ${ticket.assignedTo.lastName}` : 
                      ticket.assignedTo.username || ticket.assignedTo.name || 'Unknown Technician'}
                  </div>
                ) : (
                  <Typography color="textSecondary">Unassigned</Typography>
                )}
              </TableCell>
              <TableCell>
                <IconButton
                  size="small"
                  onClick={() => handleViewTicket(ticket._id)}
                  title="View Details"
                >
                  <VisibilityIcon />
                </IconButton>
                {showAssignButton && !ticket.assignedTo && (
                  <IconButton
                    size="small"
                    onClick={() => onAssign(ticket._id)}
                    title="Assign Ticket"
                  >
                    <AssignmentIcon />
                  </IconButton>
                )}
              </TableCell>
            </TableRow>
            );
          })}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default TicketList;
