import React, { createContext, useContext, useState, useCallback } from 'react';
import axios from 'axios';
import { TICKET_ENDPOINTS, API_URL } from '../utils/apiConfig';

const TicketContext = createContext();

export const useTickets = () => {
  const context = useContext(TicketContext);
  if (!context) {
    throw new Error('useTickets must be used within a TicketProvider');
  }
  return context;
};

export const TicketProvider = ({ children }) => {
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch all tickets
  const fetchTickets = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      console.log('Fetching tickets from:', TICKET_ENDPOINTS.GET_ALL);
      const token = localStorage.getItem('token');

      if (!token) {
        console.error('No token found for ticket fetch');
        setError('Authentication required');
        setLoading(false);
        return [];
      }

      console.log('Using token for fetch:', token.substring(0, 10) + '...');

      const response = await axios.get(TICKET_ENDPOINTS.GET_ALL, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      console.log('Tickets API response status:', response.status);
      console.log('Tickets fetched:', response.data);

      // Ensure we're dealing with an array of tickets
      const ticketsData = Array.isArray(response.data) ? response.data : 
                          (response.data.tickets ? response.data.tickets : []);

      setTickets(ticketsData);
      return ticketsData;
    } catch (err) {
      console.error('Error fetching tickets:', err);
      console.error('Error response:', err.response?.data);
      setError(err.message || 'Failed to fetch tickets');
      return [];
    } finally {
      setLoading(false);
    }
  }, []);

  // Add a new ticket to the local state
  const addTicket = useCallback((newTicket) => {
    console.log('Adding ticket to context:', newTicket);
    setTickets(prevTickets => [newTicket, ...prevTickets]);
  }, []);

  // Create a new ticket
  const createTicket = useCallback(async (ticketData) => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.post(
        TICKET_ENDPOINTS.CREATE,
        ticketData,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
            'Content-Type': 'application/json'
          }
        }
      );

      // If successful, add the new ticket to our local state
      if (response.data.ticket) {
        addTicket(response.data.ticket);
      }

      return response.data;
    } catch (err) {
      console.error('Error creating ticket:', err);
      setError(err.message || 'Failed to create ticket');
      throw err;
    } finally {
      setLoading(false);
    }
  }, [addTicket]);

  // Find user by username and get their ID
  const findUserByUsername = useCallback(async (username) => {
    try {
      const response = await axios.get(`${API_URL}/users/find?username=${username}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });
      return response.data;
    } catch (err) {
      console.error('Error finding user:', err);
      throw err;
    }
  }, []);

  // Assign ticket to a technician
  const assignTicket = useCallback(async (ticketId, technicianId) => {
    setLoading(true);
    setError(null);
    try {
      console.log(`Assigning ticket ${ticketId} to technician ${technicianId}`);

      const response = await axios.patch(
        TICKET_ENDPOINTS.ASSIGN(ticketId),
        { technicianId },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
            'Content-Type': 'application/json'
          }
        }
      );

      console.log('Ticket assignment response:', response.data);

      // Update the ticket in our local state
      setTickets(prevTickets => 
        prevTickets.map(ticket => 
          ticket._id === ticketId ? {...ticket, assignedTo: response.data.assignedTo || {_id: technicianId}} : ticket
        )
      );

      // Fetch tickets to ensure we have the latest data
      fetchTickets();

      return response.data;
    } catch (err) {
      console.error(`Error assigning ticket ${ticketId}:`, err);
      console.error('Error response:', err.response?.data);
      setError(err.message || `Failed to assign ticket ${ticketId}`);
      throw err;
    } finally {
      setLoading(false);
    }
  }, [fetchTickets]);

  // Assign all unassigned tickets to a specified technician 
  const assignAllUnassignedTickets = useCallback(async (username) => {
    setLoading(true);
    setError(null);
    try {
      // Step 1: Find the user by username
      console.log(`Finding user with username: ${username}`);
      const user = await findUserByUsername(username);
      if (!user || !user._id) {
        throw new Error(`User ${username} not found`);
      }

      console.log(`Found user: ${username} with ID: ${user._id}`);

      // Step 2: Get all unassigned tickets
      const unassignedTickets = tickets.filter(ticket => !ticket.assignedTo);
      console.log(`Found ${unassignedTickets.length} unassigned tickets`);

      if (unassignedTickets.length === 0) {
        return { message: 'No unassigned tickets found', assignedCount: 0 };
      }

      // Step 3: Assign each ticket to the user
      const assignPromises = unassignedTickets.map(ticket => 
        assignTicket(ticket._id, user._id)
      );

      await Promise.all(assignPromises);

      return { 
        message: `Successfully assigned ${unassignedTickets.length} tickets to ${username}`,
        assignedCount: unassignedTickets.length 
      };
    } catch (err) {
      console.error('Error assigning all unassigned tickets:', err);
      setError(err.message || 'Failed to assign tickets');
      throw err;
    } finally {
      setLoading(false);
    }
  }, [tickets, findUserByUsername, assignTicket]);

  const value = {
    tickets,
    loading,
    error,
    fetchTickets,
    addTicket,
    createTicket,
    assignTicket,
    assignAllUnassignedTickets
  };

  return <TicketContext.Provider value={value}>{children}</TicketContext.Provider>;
};
